INSERT INTO `sa_category` (`id`, `name`, `description`, `image_path`, `order_number`, `create_time`, `type`, `parent_id`, `update_time`, `code`, `icon_path`, `seo_description`, `seo_keyword`) VALUES
(1132793, 'Đất nước-Con người', 'Tìm hiểu về đất nước con người Pháp\n', '', 0, '2014-10-25 17:24:25', 'child', 1132802, '2015-05-05 11:29:16', '', '', 'Tìm hiểu về Đất nước và Con người tại nước Pháp để có được cái nhìn tổng quát hơn về nơi đây. ', 'dat nuoc va con nguoi phap,thong tin dat nuoc phap'),
(1132795, 'Du  học Pháp', 'Du  học Pháp', NULL, 0, '2014-10-25 17:24:25', 'main', 0, '2014-10-25 17:24:25', 'visible', NULL, 'Tư vấn du học Pháp giá rẻ 2015 sẽ mở ra con đường ngắn nhất đến với nước pháp dành cho các bạn học sinh - Cơ hội đoạt các học bổng du học Pháp giá trị.', 'du học pháp,du hoc phap,du hoc phap 2015,tu van du hoc phap'),
(1132798, 'Thông tin du học', 'Thông tin du học', NULL, 1, '2014-10-25 17:24:25', 'main', 0, '2014-10-25 17:24:25', 'visible', NULL, 'Thông tin du học Pháp, những điều cần biết trước khi sang học tập tại Pháp, chia sẻ kinh nghiệm du học Pháp.', 'thong tin du hoc phap,thông tin du học pháp,học tập tại pháp,kinh nghiệm du học pháp'),
(1132801, 'Thủ tục-Quy trình', 'Thủ tục-Quy trình', NULL, 3, '2014-10-25 17:24:25', 'main', 0, '2014-10-25 17:24:25', NULL, NULL, 'Tìm hiểu các thủ tục quy trình trước khi sang Pháp du học, những chú ý giúp đảm bảo du học thành công 100%', 'thủ tục quy trình,du học pháp,chú ý trước khi du học pháp'),
(1132802, 'Văn hóa Pháp', 'Văn hóa Pháp', NULL, 2, '2014-10-25 17:24:25', 'main', 0, '2014-10-25 17:24:25', NULL, NULL, 'Tìm hiểu nền văn hóa Pháp để có được cái nhìn tổng quát của nơi  đây và biết được lý do vì sao mọi người lại yêu quý nước Pháp đến như vậy.', 'văn hóa pháp,van hoa phap,dat nuoc phap,van hoa nguoi phap'),
(1133473, 'Chuyên gia tư vấn', 'Chuyên gia tư vấn', NULL, 0, '2014-10-25 17:24:25', 'none', 0, '2014-10-25 17:24:25', NULL, NULL, NULL, NULL),
(1138380, 'Học dự bị tại Pháp', 'Học dự bị tại Pháp\n', '', 2, '2014-10-25 17:24:25', 'child', 1132795, '2015-05-05 11:14:13', '', '', 'Thông tin các trường, thủ tục làm hồ sơ, cách xin thị thực cho việc học dự bị tại đất nước Pháp. Đảm bảo một tương lai tương sáng cho học sinh Việt Nam.', 'hoc du bi tai phap,học dự bị pháp 2015,truong hoc du bi tai phap'),
(1169448, 'Học đại học tại Pháp', 'Học đại học Pháp bằng cách nào\r\n', '', 1, '2014-10-25 17:24:25', 'child', 1132795, '2015-05-06 09:10:42', '', '', 'Thông tin các trường đại học tại pháp, thông tin học bổng dành cho các bạn học sinh tham khảo để có được sự lựa chọn và đáp ứng được nguyện vọng đi du học của mình.', 'học đại học tại pháp,hoc dai hoc tai phap,hoc tai phap'),
(1169449, 'Học cao học tại Pháp', 'Học cao học tại Pháp', '', 1, '2014-10-25 17:24:25', 'child', 1132795, '2015-05-05 11:13:30', '', '', 'Thông tin các trường đại học tại pháp, thông tin học bổng dành cho các bạn học sinh muốn học cao học tại Pháp tham khảo để có được sự lựa chọn và đáp ứng được nguyện vọng đi du học của mình.', 'hoc cao hoc tai phap,thong tin truong cao hoc phap,hoc cao hoc tai phap 2015'),
(1169450, 'Hỏi đáp', 'Hỏi đáp', NULL, 0, '2014-10-25 17:24:25', 'qNa', 0, '2014-10-25 17:24:25', NULL, NULL, 'Trả lời những câu hỏi, những thắc mắc liên quan đến việc du học Pháp, giúp học sinh hiểu rõ hơn trước khi sang Pháp học tập thành công.', 'hoi dap,hoi dap ve phap,dat nuoc phap,cau hoi ve nuoc phap'),
(1169451, 'Văn hóa - Nghệ thuật', 'Văn hóa - Nghệ thuật', '', 1, '2015-04-23 09:56:49', 'child', 1132802, '2015-05-05 11:30:46', '', '', 'Cung cấp thông tin về văn hóa nghệ thuật tại nước Pháp, giúp hiểu được những nét văn hóa đặc trưng tại nơi đây.', 'van hoa nghe thuat phap,van hoa nuoc phap,nghe thuat tai phap'),
(1169452, 'Nền giáo dục Pháp', 'Nền giáo dục Pháp', '', 2, '2015-04-23 10:01:59', 'child', 1132802, '2015-05-05 11:51:24', '', '', 'Nền giáo dục Pháp là một nền giáo dục tiên tiến và thu hút được nhiều bạn du học sinh có dự định ra nước ngoài học tập.', 'nen giao duc phap '),
(1169453, 'Tin tức du học Pháp', 'Tin tức du học Pháp', '', 0, '2015-04-23 10:06:23', 'child', 1132798, '2015-04-23 10:06:23', '', '', NULL, NULL),
(1169454, 'Học bổng du học Pháp', 'Học bổng du học Pháp', '', 1, '2015-04-23 10:10:59', 'child', 1132798, '2015-05-07 11:49:43', '', '', 'Cung cấp thông tin học bổng của các trường đại học tại Pháp giúp học sinh có sự lựa chọn cho năng lực bản thân.', 'hoc bong du hoc phap,nguon hoc bong phap,du hoc phap'),
(1169455, 'Aupair Pháp', 'Aupair Pháp', '', 2, '2015-04-23 10:11:21', 'child', 1132798, '2015-05-07 11:08:28', '', '', 'Chương trình Aupair tại Pháp là chương trình du học Pháp vừa học vừa làm dành cho những bạn đam mê tiếng Pháp.', 'aupair phap,aupair pháp,du hoc phap vua hoc vua lam,vua hoc vua lam tai phap'),
(1169456, 'Kinh nghiệm du học Pháp', 'Kinh nghiệm du học Pháp', '', 3, '2015-04-23 10:11:57', 'child', 1132798, '2015-05-07 11:04:17', '', '', 'Chia sẽ những kinh nghiệm du học Pháp của những người đã từng học tập và sinh sống tại đây.', 'kinh nghiem du hoc phap,kinh nghiem lam ho so,kinh nghiem du hoc,cách chọn trường'),
(1169458, 'Hội thảo du học Pháp', 'Hội thảo du học Pháp', '', 5, '2015-04-23 10:12:35', 'child', 1132798, '2015-05-07 11:12:35', '', '', 'Thông tin các hội thảo du học Pháp diễn ra tại Hà Nội giúp các bạn học sinh có thể trao đổi trực tiếp với đại diện trường.', 'hoi thao du hoc,hoi thao du hoc phap,to chuc hoi thao'),
(1169460, 'Thủ tục đăng ký', 'Thủ tục đăng ký', '', 0, '2015-04-23 10:15:14', 'child', 1132801, '2015-05-07 11:23:31', '', '', 'Hướng dẫn làm thủ tục đăng ký, cách chọn trường, cách làm hồ sơ, xin thị thực đi du học Pháp.', 'thủ tục đăng ký,du học pháp,đăng ký du học pháp'),
(1169461, 'Phỏng vấn', 'Phỏng vấn du học Pháp', '', 1, '2015-04-23 10:15:34', 'child', 1132801, '2015-05-07 11:22:43', '', '', 'Kinh nghiệm phỏng vấn trước khi du học Pháp, rèn luyện kỹ năng giúp học sinh tự tin trong các buổi phỏng vấn.', 'phong van,phong van du hoc phap,luyen phong van'),
(1169462, 'Visa du học Pháp', 'Visa du học Pháp', '', 2, '2015-04-23 10:15:51', 'child', 1132801, '2015-05-07 11:20:39', '', '', 'Thủ tục xin visa du học Pháp, chia sẻ kinh nghiệm xin visa du học Pháp đạt 100%.', 'visa du hoc han,visa du hoc han,thong tin du hoc han'),
(1169464, 'admin', '{"SKYPE_USERNAME":"mocxi.fab","YAHOO_USERNAME":"it_nplo_hth","MOBILE":"0986451183"}', '', 0, '2015-04-23 13:41:15', 'support', 0, '2015-04-23 13:41:15', '', '', NULL, NULL),
(1169465, 'Học nghề tại Pháp', '<p>Học BTF,học IUT tại Ph&aacute;p 2015</p>\r\n', '', 3, '2015-04-24 16:13:16', 'child', 1132795, '2015-05-05 11:15:50', '', '', 'Cung cấp thông tin các chương trình học BTF, chương trình IUT dành cho các bạn có ý định du học có được sự chuẩn bị tốt nhất trước khi sang Pháp học nghề.', 'học btf tại pháp,học iut tại pháp,hoc nghe tai phap'),
(1169466, 'Học tiếng Pháp tại Việt Phát', 'Học tiếng Pháp tại Việt Phát', '', 4, '2015-04-25 15:19:11', 'child', 1132795, '2015-05-05 11:18:08', '', '', 'Thông tin các khóa học tiếng Pháp do công ty Việt Phát tổ chức, lớp học tiếng pháp cấp tốc, lớp luyện thi tcf, luyện cách phát âm một cách nhuần nhuyễn nhất.', 'hoc tieng phap tai ha noi,luyen thi tcf,luyen tcf cap toc,lop hoc tieng phap'),
(1169467, 'Cẩm nang du học Pháp', 'Cẩm nang du học Pháp', '', 5, '2015-04-25 15:20:59', 'child', 1132795, '2015-05-05 11:25:43', '', '', 'Nhưng thông tin bổ ích về việc đi du học tại Pháp, những chia sẻ kinh nghiệm quý báu trong việc sinh sống và học tập tại đất nước có nền giáo dục hiện đại và thành công.', 'cam nang du hoc phap,kinh nghiem du hoc phap,du hoc phap'),
(1169468, 'Cơ hội việc làm tại Pháp', 'Cơ hội việc làm tại Pháp', '', 6, '2015-04-25 15:21:56', 'child', 1132798, '2015-05-07 11:14:13', '', '', 'Tìm hiễu những chỗ làm thêm tại Pháp giúp học sinh có thể vừa học vừa làm trang trải cuộc sống.', 'viec lam tai phap,lam them tai phap,co hoi viec lam tai phap');

