--
-- Table structure for table `sa_entry`
--

CREATE TABLE IF NOT EXISTS `sa_entry` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(250) DEFAULT NULL,
  `description` text,
  `content` text,
  `creator_id` bigint(20) DEFAULT NULL,
  `category_id` bigint(20) DEFAULT NULL,
  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `image_path` varchar(250) DEFAULT NULL,
  `facebook_url` varchar(500) DEFAULT NULL,
  `google_url` varchar(500) DEFAULT NULL,
  `tags` varchar(500) DEFAULT NULL,
  `is_top` tinyint(1) DEFAULT NULL,
  `display` tinyint(1) DEFAULT NULL,
  `seo_keyword` varchar(1000) DEFAULT NULL,
  `seo_description` varchar(1000) DEFAULT NULL,
  `seo_title` varchar(500) DEFAULT NULL,
  `rewrite_url` varchar(1000) DEFAULT NULL,
  `order_number` int(10) DEFAULT NULL,
  `asker` varchar(50) DEFAULT NULL,
  `asker_email` varchar(100) DEFAULT NULL,
  `category_title` varchar(255) DEFAULT NULL,
  `total_views` bigint(20) DEFAULT NULL,
  `thumbnail_url` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `category_id` (`category_id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=294 ;

