INSERT INTO `sa_entry` (`id`, `title`, `description`, `content`, `creator_id`, `category_id`, `create_time`, `update_time`, `image_path`, `facebook_url`, `google_url`, `tags`, `is_top`, `display`, `seo_keyword`, `seo_description`, `seo_title`, `rewrite_url`, `order_number`, `asker`, `asker_email`, `category_title`, `total_views`, `thumbnail_url`) VALUES
(220, 'Chia sẻ kinh nghiệm làm hồ sơ du học Pháp 2015', 'Nước Pháp thực hiện nhiều chính sách hỗ trợ sinh viên quốc tế cũng như sinh viên Việt Nam có nguyện vọng sang Pháp du học. Tuy nhiên, để chuẩn bị hồ sơ du học Pháp cũng như phỏng vấn thành công, ban cần phải học hỏi kinh nghiệm của những người đi trước hoặc qua trung tâm du học uy tín với dịch vụ chuyên nghiêp.', '<p style="text-align:justify">Nước Ph&aacute;p thực hiện nhiều ch&iacute;nh s&aacute;ch hỗ trợ sinh vi&ecirc;n quốc tế cũng như sinh vi&ecirc;n Việt Nam c&oacute; nguyện vọng sang Ph&aacute;p du học. Tuy nhi&ecirc;n, để chuẩn bị hồ sơ du học Ph&aacute;p cũng như phỏng vấn th&agrave;nh c&ocirc;ng, ban cần phải học hỏi kinh nghiệm của những người đi trước hoặc qua trung t&acirc;m du học uy t&iacute;n với dịch vụ chuy&ecirc;n nghi&ecirc;p. Với kinh nghiệm của bản th&acirc;n từng đi du học Ph&aacute;p v&agrave; l&agrave;m hồ sơ cho nhiều bạn du học Ph&aacute;p &nbsp;th&agrave;nh c&ocirc;ng, m&igrave;nh hi vọng sẽ gi&uacute;p th&ecirc;m nhiều c&aacute;c bạn kh&aacute;c nữa. &nbsp;Sau đ&acirc;y l&agrave; một số <a href="http://hosoduhocphap.edu.vn/chia-se-kinh-nghiem-lam-ho-so-du-hoc-phap-2015"><span style="color:#0000FF"><strong>kinh nghiệm l&agrave;m hồ sơ v&agrave; phỏng vấn du học Ph&aacute;p</strong></span></a>.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<h3 style="text-align:justify"><span style="font-size:16px"><strong>1. Chọn ng&agrave;nh, chọn trường đ&uacute;ng</strong></span></h3>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="truong-dai-hoc-tai-phap" src="/usrc/87/f7/_t/87f7_truong-dai-hoc-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="truong-dai-hoc-tai-phap" /></p>\r\n\r\n<p style="text-align:center">Trường đại học tại Ph&aacute;p</p>\r\n\r\n<p style="text-align:justify"><br />\r\nC&oacute; rất &nbsp;nhiều bạn bị trường từ chối v&igrave; sai lầm ngay từ bước đầu ti&ecirc;n n&agrave;y v&igrave; vậy bạn cần nắm r&otilde; &nbsp;những nguy&ecirc;n tắc khi t&igrave;m kiếm v&agrave; lựa chọn ng&agrave;nh, trường đại học tai Ph&aacute;p , cụ thể như sau:</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>&nbsp;- T&igrave;m hiểu kỹ &nbsp;ng&agrave;nh nghề m&igrave;nh &nbsp;mong muốn học v&agrave; đăng k&iacute; khi đi du học Ph&aacute;p.</strong></p>\r\n\r\n<p style="text-align:justify"><br />\r\nC&oacute; vẻ như &nbsp;nghe rất đơn giản nhưng nhiều học sinh sinh vi&ecirc;n &nbsp;chưa coi trọng việc đinh hướng nghề nghiệp. C&aacute;c bạn &nbsp;kh&ocirc;ng hiểu r&otilde; m&igrave;nh muốn học c&aacute;i g&igrave;, học xong sẽ l&agrave;m c&ocirc;ng việc g&igrave; n&ecirc;n c&oacute; những ph&acirc;n v&acirc;n giữa c&aacute;c ng&agrave;nh chẳng hạn Quản trị hay ng&agrave;nh Kinh doanh hoặc &nbsp;Kinh tế,&hellip; &nbsp;Nếu bạn kh&ocirc;ng biết ng&agrave;nh n&agrave;o ra trường sẽ &nbsp;l&agrave;m c&ocirc;ng việc g&igrave; th&igrave; coi như học kh&ocirc;ng c&oacute; mục đ&iacute;ch. Đứng trước nhiều lựa chọn trong hệ thống đ&agrave;o tạo đồ sộ của Ph&aacute;p, nhiều bạn chọn 2-3 ng&agrave;nh kh&aacute;c nhau tại c&ugrave;ng một trường. &nbsp;Đ&acirc;y l&agrave; sai lầm dẫn đến những sai s&oacute; &nbsp;trong hồ sơ v&agrave; dễ bị trường từ chối &nbsp;do sinh &nbsp;vi&ecirc;n kh&ocirc;ng c&oacute; kế hoạch học tập cũng như &nbsp;định hướng nghề nghiệp cụ thể &nbsp;r&otilde; r&agrave;ng.</p>\r\n\r\n<h3 style="text-align:justify"><br />\r\n<span style="font-size:16px"><strong>Một v&agrave;i &nbsp;lưu &yacute; quan trọng khi l&agrave;m thủ tục hồ sơ v&agrave; phỏng vấn Campus để đi &nbsp;<a href="http://hosoduhocphap.edu.vn/du-hoc-phap"><span style="color:#0000FF">du học Ph&aacute;p&nbsp;</span></a></strong></span></h3>\r\n\r\n<p style="text-align:justify"><br />\r\nPh&aacute;p kh&ocirc;ng chỉ c&oacute; c&aacute;c trường Đại học tổng hợp ( Les universites) m&agrave; c&ograve;n rất nhiều lựa chọn kh&aacute;c như c&aacute;c trường lớn Grandes Ecoles: Hệ thống trường Thương mại (Ecoles de Commerce/Business Schools), Hệ thống trường chuy&ecirc;n ng&agrave;nh đặc th&ugrave;, nghệ thuật (Ecoles specialisees).</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>-&nbsp;&nbsp; &nbsp;N&ecirc;n chọn trường ph&ugrave; hợp với năng lực v&agrave; t&igrave;m hiểu đầy đủ c&aacute;c &nbsp;y&ecirc;u cầu, thời hạn nộp nộp hồ sơ</strong></p>\r\n\r\n<p style="text-align:justify"><br />\r\nViệc quan trọng l&agrave; bạn kh&ocirc;ng chỉ t&igrave;m th&ocirc;ng tin về trường qua Campusfrance m&agrave; bạn cần thu thập c&aacute;c th&ocirc;ng tin đầy đủ về trường bạn dự định chọn th&ocirc;ng qua trang web của trường &nbsp;cũng như c&aacute;c anh chị đi trươc &nbsp;để xem điều kiện đầu v&agrave;o của trường nhằm &nbsp;so s&aacute;nh với năng lực hồ sơ của bạn. C&oacute; nhiều học sinh c&oacute; ước muốn đi du học Ph&aacute;p &nbsp;bị loại ngay từ kh&acirc;u lọc hồ sơ do kh&ocirc;ng đ&aacute;p ứng y&ecirc;u cầu cơ bản khi nộp như tr&igrave;nh độ tiếng Ph&aacute;p hay lộ tr&igrave;nh học...</p>\r\n\r\n<p style="text-align:justify"><br />\r\n&nbsp;<strong>- Nếu bạn muốn đăng k&iacute; kh&oacute;a học</strong> bằng chương tr&igrave;nh t tiếng Anh hoặc bằng hai ng&ocirc;n ngữ &nbsp;Anh &ndash; Ph&aacute;p tại Ph&aacute;p th&igrave; số lượng c&aacute;c trường kh&ocirc;ng nhiều v&agrave; hầu hết c&aacute;c trường đều y&ecirc;u c&acirc;u gửi cả hồ sơ giấy v&agrave; hồ sơ online trực tiếp tr&ecirc;n web của trường đồng thời tr&ecirc;n campusfrance Viet Nam.<br />\r\nNhững năm gần đ&acirc;y, học sinh sang Ph&aacute;p theo học chưng tr&igrave;nh tiếng Anh ng&agrave;y c&agrave;ng đ&ocirc;ng v&igrave; chi ph&iacute; học tại Ph&aacute;p thấp do hỗ trợ của ch&iacute;nh phủ cho sinh vi&ecirc;n nước ngo&agrave;i v&agrave; bản địa như nhau (ho&agrave;n to&agrave;n kh&aacute;c với c&aacute;c nước kh&aacute;c), 1 visa v&agrave;o tất cả &nbsp;27 nước ch&acirc;u &Acirc;u &nbsp;trong khối Schengen &nbsp;(1 visa cơ hội kh&aacute;m ph&aacute; v&agrave; l&agrave;m việc tại nhiều nước), chất lượng bằng cấp kh&ocirc;ng thu k&eacute;m (kiểm định chất lượng to&agrave;n cầu với c&aacute;c chứng chỉ AACSB, EPAS,&hellip;). Học sinh sang Ph&aacute;p du học bằng chương tr&igrave;nh tiếng Anh kh&ocirc;ng cần phải c&oacute; chứng chỉ tiếng Ph&aacute;p như trước kia cũng l&agrave; điều thuận lợi v&igrave; c&aacute;c bạn tập trung v&agrave;o &ocirc; tiếng anh để c&oacute; thể đi du học Ph&aacute;p.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="phong-canh-dat-nuoc-phap" src="/usrc/ee/a2/_p/eea2_phong-canh-dat-nuoc-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="phong-canh-dat-nuoc-phap" /></p>\r\n\r\n<p style="text-align:center">Phong cảnh đất nước Ph&aacute;p</p>\r\n\r\n<h3 style="text-align:justify"><strong>&nbsp;<span style="font-size:16px">2. L&agrave;m hồ sơ xin học, qua web của trường, gửi hồ sơ giấy v&agrave; hồ sơ Campusfrance.&nbsp;</span></strong></h3>\r\n\r\n<p style="text-align:justify"><br />\r\nBạn cần đầu tư c&ocirc;ng sức để ho&agrave;n thiện hồ sơ của m&igrave;nh thật cẩn thận v&agrave; chu đ&aacute;o cụ thể l&agrave; kế hoạch học tập tại Ph&aacute;p, kế hoạch nghề nghiệp sau khi ra trường v&igrave; du học Ph&aacute;p l&agrave; cơ hội lớn &nbsp;trong đời bạn để hiện thực hoa ước mơ của m&igrave;nh. Nhiều sinh &nbsp;vi&ecirc;n kh&ocirc;ng coi trọng &nbsp;vấn đề hồ sơ n&agrave;y v&agrave; nghĩ rằng điểm phẩy quyết định v&agrave; l&agrave;m hồ sơ cẩu thả n&ecirc;n bị trường từ chối. Bạn n&ecirc;n chăm ch&uacute;t cho c&aacute;c nội dung thể hi&ecirc;n động lực của &nbsp;bản th&acirc;n: thư tr&igrave;nh b&agrave;y động cơ tốt &nbsp;(lettre de motivation), thư tr&igrave;nh b&agrave;y kế hoạch học tập &nbsp;r&otilde; r&agrave;ng (projet d&rsquo;etudes) v&agrave; thư tr&igrave;nh b&agrave;y kế hoạch nghề nghiệp tương lai &nbsp;cụ thể (projet professionel). Với hồ sơ trực tuyến tr&ecirc;n web của trường, bạn kh&ocirc;ng chỉ thể hiện với trường về năng lực v&agrave; nguyện vọng bản th&acirc;n m&agrave; c&ograve;n với &nbsp;đại sứ qu&aacute;n Ph&aacute;p để xin visa. Với hồ sơ trực tiếp, bạn cần chuẩn bị kĩ cả kh&acirc;u phỏng vấn. Lưu &yacute; rằng nhiều trường y&ecirc;u cầu gửi hồ sơ cả trực tuyến qua Cơ quan quản l&yacute; du học Ph&aacute;p v&agrave; hồ sơ giấy.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<h3 style="text-align:justify"><strong>&nbsp; <span style="font-size:16px">3. &nbsp;Thực hiện phỏng vấn Campusfrance:&nbsp;</span></strong></h3>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="phong-van-campusfrance" src="/usrc/a5/dd/_p/a5dd_phong-van-campusfrance.jpg" style="margin:5px auto; position:relative; width:90%" title="phong-van-campusfrance" /></p>\r\n\r\n<p style="text-align:center">Phỏng vấn CampusFrance</p>\r\n\r\n<p style="text-align:justify"><br />\r\nVới những sinh vi&ecirc;n đu học Ph&aacute;p, phỏng vấn với trường hay cơ quan quản l&yacute; du học Ph&aacute;p l&agrave; cơ hội để họ thể hiện bản th&acirc;n nhằm n&ecirc;u bật những điểm mạnh của hồ sơ, năng lực bản th&acirc;n, đặc biệt với những sinh vi&ecirc;n &nbsp;c&oacute; hồ sơ, bảng điểm kh&ocirc;ng thật sự xuất sắc; nhưng cũng c&oacute; nhiều bạn nh&uacute; nh&aacute;t, kh&ocirc;ng tự tin n&ecirc;n chưa thể hiện được m&igrave;nh trong cuộc phỏng vấn do nhiều yếu tố như &nbsp;t&acirc;m l&yacute;, c&aacute;ch thể hiện hay ngoại ngữ chưa thật sự tốt. Vậy h&atilde;y b&igrave;nh tĩnh v&agrave; t&igrave;m &nbsp;hiểu bản chất của mỗi cuộc phỏng vấn, hiểu r&otilde; hồ sơ &nbsp;du học của m&igrave;nh v&agrave; &nbsp;mong muốn được theo học tại Ph&aacute;p.&nbsp;</p>\r\n\r\n<hr />\r\n<p style="text-align:justify"><strong>Để biết th&ecirc;m th&ocirc;ng tin chi tiết li&ecirc;n quan tới &nbsp;<a href="http://hosoduhocphap.edu.vn/">hồ sơ du học Ph&aacute;p</a> v&agrave; c&aacute;c lớp luyện phỏng vấn &nbsp;c&aacute;c bạn c&oacute; thể li&ecirc;n hệ.&nbsp;</strong></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong><span style="color:rgb(0, 0, 205)">C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t - VTI</span></strong></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Địa chỉ&nbsp;</strong>&nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;&nbsp;Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Website</strong>&nbsp;&nbsp;&nbsp; :&nbsp;</span></span>http://hosoduhocphap.edu.vn/</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Email</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;duhocvietphatvti@gmail.com&nbsp;</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>ĐT &nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>FanPage</strong>&nbsp;&nbsp; : &nbsp;&nbsp;</span></span>https://www.facebook.com/DuhocVietPhatVTI</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Hotline</strong>&nbsp;&nbsp; &nbsp; :&nbsp;<strong><span style="color:red">0983 102 258</span></strong></span></span></p>\r\n', 1, 1169456, '2015-04-23 16:25:20', '2015-04-24 11:57:02', '/usrc/2c/7a/_l/2c7a_lam-ho-so-du-hoc-phap.jpeg', '', '', 'kinh nghiem lam ho so phap,du hoc phap 2015,lam ho so du hoc phap,kinh nghiem lam ho so', 0, 1, 'kinh nghiem lam ho so phap,du hoc phap 2015,lam ho so du hoc phap,kinh nghiem lam ho so', 'Chia sẻ kinh nghiệm làm hồ sơ du học Pháp 2015 để học sinh có được sự chuẩn bị tốt nhất ngay từ những bước đi đầu tiên trên con đường du học của mình.', 'Chia sẻ kinh nghiệm làm hồ sơ du học Pháp 2015', 'chia-se-kinh-nghiem-lam-ho-so-du-hoc-phap-2015', 2, '', '', NULL, 0, '/usrc/2c/7a/_l/thumbnail/2c7a_lam-ho-so-du-hoc-phap.jpeg'),
(221, 'Aupair Pháp - Chương trình vừa học vừa làm tại Pháp', 'Chương trình vừa học vừa làm tại Pháp - "Jeune aupair” là chương trình du học đang được các bạn trẻ quan tâm và hướng đến. Đó là chương trình - giải pháp tối ưu nhằm giải quyết hai khó khăn chính mà các học sinh – sinh viên thường gặp phải, đó là trình độ ngôn ngữ và khả năng tài chính. ', '<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Chương tr&igrave;nh du học Ph&aacute;p vừa học vừa l&agrave;m &quot;Jeune aupair&rdquo; tại Ph&aacute;p l&agrave; chương tr&igrave;nh du học đang được c&aacute;c bạn trẻ quan t&acirc;m v&agrave; hướng đến. Đ&oacute; l&agrave; chương tr&igrave;nh - giải ph&aacute;p tối ưu nhằm giải quyết hai kh&oacute; khăn ch&iacute;nh m&agrave; c&aacute;c học sinh &ndash; sinh vi&ecirc;n thường gặp phải, đ&oacute; l&agrave; tr&igrave;nh độ ng&ocirc;n ngữ v&agrave; khả năng t&agrave;i ch&iacute;nh.&nbsp;</p>\r\n\r\n<h3 style="text-align:justify"><br />\r\n<span style="font-size:14px"><strong><span style="font-size:16px">CHƯƠNG TR&Igrave;NH VỪA HỌC VỪA &nbsp;L&Agrave;M TẠI PH&Aacute;P&nbsp;&ndash; JEUNE AUPAIR</span></strong></span></h3>\r\n\r\n<p style="text-align:justify"><br />\r\nNhư c&aacute;c bạn đ&atilde; biết, hiện nay c&oacute; rất nhiều học sinh &ndash; sinh vi&ecirc;n c&oacute; nhu cầu đi du học Ph&aacute;p v&igrave; nhiều mục đ&iacute;ch kh&aacute;c nhau như muốn hiểu biết s&acirc;u hơn về lĩnh vực m&agrave; bản th&acirc;n đ&atilde; học hay l&agrave;m việc, v&igrave; sở th&iacute;ch,v&igrave; c&oacute; người th&acirc;n b&ecirc;n Ph&aacute;p&hellip;Nhưng kh&ocirc;ng phải học sinh &ndash; sinh vi&ecirc;n n&agrave;o cũng c&oacute; thể giải quyết được nhu cầu du học của m&igrave;nh do nhiều vấn đề v&agrave; kh&oacute; khăn kh&aacute;c nhau. Trong đ&oacute;, hai &nbsp;vấn đề quan trọng nhất m&agrave; c&aacute;c bạn gặp phải l&agrave; tr&igrave;nh độ ng&ocirc;n ngữ v&agrave; khả năng t&agrave;i ch&iacute;nh. D&ugrave; l&agrave; c&aacute;c trường c&ocirc;ng lập tại Ph&aacute;p học ph&iacute; rẻ, sinh hoạt được hỗ trợ của nh&agrave; nước, nhưng kh&ocirc;ng phải sinh vi&ecirc;n n&agrave;o y&ecirc;u th&iacute;ch nước ph&aacute;p cũng c&oacute; thể c&oacute; đủ t&agrave;i ch&iacute;nh c&ograve;n lại để thực hiện niềm đam m&ecirc; của bản th&acirc;n.<br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align:center"><img alt="aupair-tai-phap" src="/usrc/79/69/_a/7969_aupair-tai-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="aupair-tai-phap" /><br />\r\nAupair Ph&aacute;p&nbsp;chơi với trẻ con&nbsp;&nbsp;- Đi du lịch c&ugrave;ng gia đ&igrave;nh</p>\r\n\r\n<p style="text-align:justify"><br />\r\nCh&iacute;nh v&igrave; vậy, c&ocirc;ng ty du học Việt Ph&aacute;t xin giới thiệu chương tr&igrave;nh <a href="http://hosoduhocphap.edu.vn/aupair-phap-chuong-trinh-vua-hoc-vua-lam-tai-phap"><strong>du học vừa học vừa l&agrave;m tại Ph&aacute;p</strong></a>, nhằm đ&aacute;p ứng nhu cầu của sinh vi&ecirc;n muốn thực hiện ước mơ du học Ph&aacute;p, vừa được n&acirc;ng cao &ndash; cải thiện khả năng ng&ocirc;n ngữ, vừa được t&igrave;m hiểu văn h&oacute;a &ndash; con người Ph&aacute;p v&agrave; c&oacute; th&ecirc;m một khoản thu nhập để l&agrave;m nền tảng cho kế hoạch học tập tương lai.<br />\r\nĐ&Oacute; CH&Iacute;NH L&Agrave; CHƯƠNG TR&Igrave;NH VỪA HỌC VỪA L&Agrave;M &ldquo; JEUNE AUPAIR&rdquo;- Vừa đi học - vừa được đi du lịch m&agrave; vẫn kiếm được tiền.</p>\r\n\r\n<h3 style="text-align:justify"><br />\r\n<strong><span style="font-size:16px">CHƯƠNG TR&Igrave;NH JEUNE AUPAIR L&Agrave; G&Igrave;?</span></strong></h3>\r\n\r\n<p style="text-align:justify"><br />\r\n- &quot;&nbsp;AUPAIR &quot;&nbsp;trong tiếng ph&aacute;p c&oacute; nghĩa l&agrave; &quot;&nbsp;SONG H&Agrave;NH &quot;.<br />\r\n- &quot;JEUNE AUPAIR&quot; l&agrave; chương tr&igrave;nh hỗ trợ c&aacute;c bạn trẻ c&oacute; mong muốn sống trong một gia đ&igrave;nh ở nước ngo&agrave;i (3 th&aacute;ng v&agrave;o dịp h&egrave;, 6 th&aacute;ng hoặc 1 năm) &ndash; coi như một th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh. C&ocirc;ng việc của một jeune aupair l&agrave; tr&ocirc;ng trẻ, phụ gi&uacute;p việc nh&agrave; khi sống c&ugrave;ng gia đ&igrave;nh. B&ecirc;n cạnh đ&oacute;, c&aacute;c jeune aupair tại ph&aacute;p được ăn uống v&agrave; sinh hoạt miễn ph&iacute;, được trả lương, được đ&oacute;ng bảo hiểm, được đi du lịch v&agrave; được gia đ&igrave;nh sắp xếp cho c&aacute;c lớp học th&ecirc;m về ng&ocirc;n ngữ, văn h&oacute;a.<br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="tre-con-tai-phap" src="/usrc/d8/4c/_t/d84c_tre-con-tai-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="tre-con-tai-phap" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh trẻ em tại Ph&aacute;p</p>\r\n\r\n<h3 style="text-align:justify"><br />\r\n<span style="font-size:16px"><strong>ƯU ĐIỂM CHƯƠNG TR&Igrave;NH JEUNE AUPAIR.</strong></span></h3>\r\n\r\n<p style="text-align:justify"><br />\r\n- Học v&agrave; n&acirc;ng cao ngoại ngữ trong m&ocirc;i trường bản ngữ.<br />\r\n- Kh&ocirc;ng mất chi ph&iacute; trong khi sống c&ugrave;ng người bản xứ.<br />\r\n- L&agrave;m việc 5h/ng&agrave;y, được nghỉ 2-3 ng&agrave;y/tuần, được nghỉ 4 tuần/ năm.<br />\r\n- Được trả lương 300-400 euros/th&aacute;ng v&agrave; được đ&oacute;ng bảo hiểm.<br />\r\n- Được tham gia c&aacute;c buổi trao đổi giữa c&aacute;c bạn aupair đến từ c&aacute;c nước kh&aacute;c nhau.<br />\r\n- Được gia hạn visa du học sau khi kết th&uacute;c chương tr&igrave;nh.<br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="gia-dinh-nguoi-phap" src="/usrc/38/fb/_g/38fb_gia-dinh-nguoi-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="gia-dinh-nguoi-phap" /></p>\r\n\r\n<p style="text-align:center">Một gia đ&igrave;nh nhận Aupair tại Ph&aacute;p</p>\r\n\r\n<p style="text-align:justify"><br />\r\n<strong>ĐIỀU KIỆN THAM GIA CHƯƠNG TR&Igrave;NH.</strong></p>\r\n\r\n<p style="text-align:justify"><br />\r\n- C&aacute;c bạn nữ từ 19-28 tuổi t&iacute;nh từ thời điểm nộp hồ sơ, đang l&agrave; sinh vi&ecirc;n Đại học, Cao đẳng hoặc đ&atilde; tốt nghiệp.<br />\r\n- Y&ecirc;u qu&yacute; trẻ em, c&oacute; kinh nghiệm chăm s&oacute;c trẻ v&agrave; biết l&agrave;m nội trợ.<br />\r\n- Giao tiếp tiếng Ph&aacute;p ( nếu chưa biết c&aacute;c bạn sẽ được đ&agrave;o tạo tại trung t&acirc;m ngoại ngữ c&ocirc;ng ty Việt Ph&aacute;t VTI ).<br />\r\n- Kh&ocirc;ng vi phạm ph&aacute;p luật, c&oacute; tinh thần tr&aacute;ch nhiệm v&agrave; phẩm chất đạo đức tốt.</p>\r\n\r\n<p style="text-align:justify">Đến với c&ocirc;ng ty tư vấn du học Việt Ph&aacute;t, c&aacute;c bạn sẽ được gặp gỡ, giao lưu c&ugrave;ng c&aacute;c bạn đ&atilde; tham gia <a href="http://hosoduhocphap.edu.vn/aupair-phap-chuong-trinh-vua-hoc-vua-lam-tai-phap"><strong>chương tr&igrave;nh aupair</strong></a> tại c&aacute;c nước tr&ecirc;n thế giới như Đức, Ph&aacute;p, Anh..... Họ sẽ chia sẻ &nbsp;những trải nghiệm m&igrave;nh c&oacute; được trong khi học tập v&agrave; l&agrave;m việc của họ. Ch&uacute;ng t&ocirc;i tự h&agrave;o l&agrave; cầu nối của c&aacute;c bạn y&ecirc;u th&iacute;ch c&aacute;c nước tr&ecirc;n thế giới n&oacute;i chung v&agrave; &nbsp;đất nước xinh đẹp h&igrave;nh lục lăng &ndash; Ph&aacute;p n&oacute;i ri&ecirc;ng.</p>\r\n\r\n<hr />\r\n<p style="text-align:justify"><br />\r\n<strong>Để t&igrave;m hiểu th&ocirc;ng tin chi tiết về chương tr&igrave;nh vừa học- vừa l&agrave;m &quot;Jeune aupair&rdquo; c&ugrave;ng c&aacute;c chương tr&igrave;nh như <a href="http://hosoduhocphap.edu.vn/du-hoc-phap">du học Ph&aacute;p gi&aacute; rẻ</a> v&agrave; th&ocirc;ng tin du học Ph&aacute;p 2015 mới nhất, h&atilde;y li&ecirc;n hệ ngay với ch&uacute;ng t&ocirc;i để được tư vấn miễn ph&iacute; nh&eacute; :</strong></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong><span style="color:rgb(0, 0, 205)">C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t - VTI</span></strong></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Địa chỉ&nbsp;</strong>&nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;&nbsp;Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Website</strong>&nbsp;&nbsp;&nbsp; :&nbsp;</span></span>http://hosoduhocphap.edu.vn/</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Email</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;duhocvietphatvti@gmail.com&nbsp;</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>ĐT &nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>FanPage</strong>&nbsp;&nbsp; : &nbsp;&nbsp;</span></span>https://www.facebook.com/DuhocVietPhatVTI</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Hotline</strong>&nbsp;&nbsp; &nbsp; :&nbsp;<strong><span style="color:red">0983 102 258</span></strong></span></span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 1169455, '2015-04-23 18:20:35', '2015-04-24 11:43:28', '/usrc/0b/07/_a/0b07_aupair-tai-phap-1.jpg', '', '', 'aupair phap,aupair pháp,du hoc phap vua hoc vua lam,vua hoc vua lam tai phap', 1, 1, 'aupair phap,aupair pháp,du hoc phap vua hoc vua lam,vua hoc vua lam tai phap', 'Aupair Pháp là chương trình vừa học vừa làm tại Pháp dành cho các bạn học sinh ham học tiếng Pháp. Aupair là con đường ngắn nhất để bạn thực hiện ước mơ du học Pháp của mình.', 'Aupair Pháp - Chương trình vừa học vừa làm tại Pháp', 'aupair-phap-chuong-trinh-vua-hoc-vua-lam-tai-phap', 3, '', '', NULL, 0, '/usrc/0b/07/_a/thumbnail/0b07_aupair-tai-phap-1.jpg'),
(222, 'Kinh nghiệm làm hồ sơ  chương trình Aupair Pháp - Vừa học vừa làm', 'Chương trình du học vừa học - vừa làm " Jeune aupair" là chương trình đang được nhiều bạn trẻ quan tâm trên thế giới nói chung và Việt Nam nói riêng. Nó là chương trình du học với nhiều ưu điểm như được sống trong môi trường văn hóa cùng gia đình người bản địa, được đi du lịch, được khám phá văn hóa mới với nguồn  thu nhập ổn định và được đóng bảo hiểm hấp dẫn trong khi chỉ với công việc trông trẻ.', '<p style="text-align:justify"><br />\r\nChương tr&igrave;nh du học vừa học - vừa l&agrave;m &quot; Jeune aupair&quot; l&agrave; chương tr&igrave;nh đang được nhiều bạn trẻ quan t&acirc;m tr&ecirc;n thế giới n&oacute;i chung v&agrave; Việt Nam n&oacute;i ri&ecirc;ng. N&oacute; l&agrave; chương tr&igrave;nh du học với nhiều ưu điểm như được sống trong m&ocirc;i trường văn h&oacute;a c&ugrave;ng gia đ&igrave;nh người bản địa, được đi du lịch, được kh&aacute;m ph&aacute; văn h&oacute;a mới với nguồn &nbsp;thu nhập ổn định v&agrave; được đ&oacute;ng bảo hiểm hấp dẫn trong khi chỉ với c&ocirc;ng việc tr&ocirc;ng trẻ. Qu&aacute; tr&igrave;nh t&igrave;m gia đ&igrave;nh bản địa kh&ocirc;ng dễ d&agrave;ng, nhưng khi c&aacute;c bạn t&igrave;m được rồi, một kh&oacute; khăn cần đối mặt đ&oacute; l&agrave; qu&aacute; tr&igrave;nh l&agrave;m hồ sơ. Việt Ph&aacute;t sẽ t&oacute;m tắt cho c&aacute;c bạn một số<strong> kinh nghiệm khi l&agrave;m hồ sơ aupair Ph&aacute;p</strong> - chương tr&igrave;nh du học vừa học vừa l&agrave;m.</p>\r\n\r\n<p style="text-align:justify"><br />\r\nQuy tr&igrave;nh l&agrave;m hồ sơ aupair thường nhanh hơn rất nhiều so với quy tr&igrave;nh l&agrave;m <a href="http://hosoduhocphap.edu.vn/"><strong>hồ sơ du học tại Ph&aacute;p</strong></a> v&agrave; kh&ocirc;ng phải tu&acirc;n thủ theo deadline ngặt ngh&egrave;o của c&aacute;c trường đại học. Tuy nhi&ecirc;n &nbsp;Aupair Ph&aacute;p y&ecirc;u cầu hồ sơ phải rất c&acirc;n thận v&agrave; chi tiết cũng như y&ecirc;u cầu về giao tiếp tiếng Ph&aacute;p phải kh&aacute; tốt. Việt Ph&aacute;t xin chia sẻ b&iacute; quyết v&agrave; kinh nghiệm l&agrave;m hồ sơ Aupair để con đường du học Ph&aacute;p của c&aacute;c bạn được nhanh ch&oacute;ng v&agrave; hiệu quả nhất.<br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align:justify">Ngo&agrave;i việc chuẩn bị hồ sơ bao gồm rất nhiều loại giấy tờ, th&igrave; một điều rất quan trọng để g&acirc;y ấn tượng v&agrave; thu h&uacute;t với c&aacute;c gia đ&igrave;nh tốt tại Ph&aacute;p, đ&oacute; l&agrave; ảnh chụp c&ugrave;ng c&aacute;c em nhỏ v&agrave; thư viết cho c&aacute;c gia đ&igrave;nh. Khi tham gia chương tr&igrave;nh, Việt Ph&aacute;t sẽ cung cấp cho c&aacute;c bạn c&aacute;c th&ocirc;ng tin về c&aacute;c gia đ&igrave;nh trong chương tr&igrave;nh, c&aacute;c bạn phải gửi thư giới thiệu về bản th&acirc;n, động lực tham gia chương tr&igrave;nh. Bức thư n&agrave;y rất quan trọng, l&agrave; yếu tố quyết định gi&uacute;p c&aacute;c bạn được lựa chọn bởi c&aacute;c gia đ&igrave;nh b&ecirc;n Ph&aacute;p. Ch&iacute;nh v&igrave; vậy, để tạo ấn tượng tốt, c&aacute;c bạn n&ecirc;n chụp những bức ảnh tươi tắn, năng động, &aacute;nh s&aacute;ng tốt c&ugrave;ng với trẻ em, hay những hoạt động ngoại kh&oacute;a của bản th&acirc;n.&nbsp;</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="trong-tre-tai-phap-aupair" src="/usrc/03/37/_t/0337_trong-tre-tai-phap-aupair.jpg" style="margin:5px auto; position:relative; width:90%" title="trong-tre-tai-phap-aupair" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh Aupair Ph&aacute;p vui chơi c&ugrave;ng trẻ em</p>\r\n\r\n<p style="text-align:justify"><br />\r\nQua những bức ảnh, c&aacute;c gia đ&igrave;nh Ph&aacute;p sẽ thấy được Aupair của Việt Nam l&agrave; những sinh vi&ecirc;n năng động, y&ecirc;u trẻ v&agrave; ham hoạt động. C&aacute;c bạn c&oacute; thể chụp ảnh c&ugrave;ng hướng dẫn &ndash; k&egrave;m cặp trẻ học b&agrave;i, vui chơi với với trẻ như chơi c&ugrave;ng trẻ ở c&ocirc;ng vi&ecirc;n, hoạt động thể thao như đi xe đạp, đ&aacute; b&oacute;ng... Hoặc c&aacute;c bạn c&oacute; thể c&oacute; thể tạo ấn tượng bằng những bức ảnh với những m&oacute;n ăn y&ecirc;u th&iacute;ch do ch&iacute;nh c&aacute;c bạn l&agrave;m. &nbsp;Bức ảnh với nụ cười hiền dịu v&agrave; &aacute;nh mắt tr&igrave;u mến trẻ nhỏ sẽ để lại ấn tượng tốt với c&aacute;c gia đ&igrave;nh Ph&aacute;p.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Vậy l&agrave;m thế n&agrave;o để g&acirc;y ấn tượng đầu ti&ecirc;n với họ qua thư gửi gia đ&igrave;nh?. C&aacute;c bạn c&oacute; thể bắt đầu bằng c&aacute;ch giới thiệu sơ qua về bản th&acirc;n v&agrave; gia đ&igrave;nh m&igrave;nh tại Việt Nam. &nbsp;V&iacute; dụ &nbsp;như những mối quan t&acirc;m, những th&oacute;i quen v&agrave; sở th&iacute;ch của c&aacute;c bạn, đặc biệt những sở th&iacute;ch li&ecirc;n quan đến chương tr&igrave;nh vừa học vừa l&agrave;m aupair tại Ph&aacute;p sẽ l&agrave; mối quan t&acirc;m v&agrave; ch&uacute; &yacute; với c&aacute;c gia đ&igrave;nh tại Ph&aacute;p, v&iacute; dụ như những sở th&iacute;ch đặc trưng về y&ecirc;u th&iacute;ch trẻ em, gọn g&agrave;ng, nấu &nbsp;nướng đặc biệt l&agrave; c&aacute;c m&oacute;n ăn truyền thống Việt Nam cũng như sở th&iacute;ch về văn h&oacute;a &ndash; ngoại ngữ Ph&aacute;p&hellip;<br />\r\n&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="aupair-trong-tre-tai-phap" src="/usrc/4b/01/_a/4b01_aupair-trong-tre-tai-phap.jpg" style="margin:5px auto; position:relative; width:90%" title="aupair-trong-tre-tai-phap" /></p>\r\n\r\n<p style="text-align:center">Trẻ em đang đi chơi c&ugrave;ng Aupair rất vui vẻ</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Vốn hiểu biết về ng&ocirc;n ngữ văn h&oacute;a Ph&aacute;p &nbsp;v&agrave; động lực tham gia <a href="http://hosoduhocphap.edu.vn/aupair-phap-chuong-trinh-vua-hoc-vua-lam-tai-phap"><strong>chương tr&igrave;nh Aupair Ph&aacute;p</strong></a> sẽ g&acirc;y sự ch&uacute; &yacute; với c&aacute;c gia đ&igrave;nh. Việc &nbsp;viết thư qua lại với c&aacute;c gia đ&igrave;nh cần lu&ocirc;n lu&ocirc;n thể hiện sự quan t&acirc;m chu đ&aacute;o, &acirc;n cần đến từng th&agrave;nh vi&ecirc;n. Người ta thương n&oacute;i &ldquo; cho đi c&aacute;i g&igrave; th&igrave; sẽ nhận được c&aacute;i đ&oacute;&rdquo; n&ecirc;n ch&uacute;ng ta cần phải quan t&acirc;m đến họ trước th&igrave; họ chắc chắn sẽ rất quan t&acirc;m đến bạn. Việc cả hai b&ecirc;n c&ugrave;ng hiểu nhau, h&agrave;i l&ograve;ng v&agrave; lựa chọn lẫn nhau sẽ l&agrave;m cho quả tr&igrave;nh l&agrave;m hồ sơ nhanh ch&oacute;ng v&agrave; hiệu quả hơn r&acirc;t nhiều. V&agrave; điều cuối c&ugrave;ng đ&oacute; l&agrave; khả năng v&agrave; kinh nghiệm của bản th&acirc;n c&ugrave;ng với lợi &iacute;ch của chương tr&igrave;nh mang lại &nbsp;cho &nbsp;bản th&acirc;n ch&iacute;nh l&agrave; phần m&agrave; gia đ&igrave;nh nu&ocirc;i đ&aacute;nh gi&aacute; rất cao. C&aacute;c bạn n&ecirc;n n&ecirc;u &nbsp;chi tiết về phần kinh nghiệm, như: dạy trẻ học b&agrave;i, &nbsp;vui chơi với &nbsp;trẻ &nbsp;nhỏ trẻ đến trường, , nấu ăn...cần m&ocirc; tả một c&aacute;ch hợp logic. Tham gia chương tr&igrave;nh, c&aacute;c bạn cũng n&ecirc;n t&igrave;m hiểu th&ocirc;ng tin về trẻ em như một số c&aacute;ch xử l&iacute; nhanh khi trẻ ốm, hay t&acirc;m l&iacute; trẻ v.v&hellip; Điều đ&oacute; cũng l&agrave; yếu tố quan trọng gi&uacute;p c&aacute;c bạn tăng cơ hội v&agrave; l&agrave;m điểm nhấn cho hồ sơ của c&aacute;c bạn khi gửi sang c&aacute;c gia đ&igrave;nh b&ecirc;n Ph&aacute;p.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<hr />\r\n<p style="text-align:justify"><br />\r\n<strong>Vậy để biết th&ecirc;m th&ocirc;ng tin chi tiết về chương tr&igrave;nh <a href="http://hosoduhocphap.edu.vn/du-hoc-phap">du học Ph&aacute;p</a> &ndash; vừa học vừa l&agrave;m &ndash; Jeune aupair, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ :</strong></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong><span style="color:rgb(0, 0, 205)">C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t - VTI</span></strong></span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Địa chỉ&nbsp;</strong>&nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;&nbsp;Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Website</strong>&nbsp;&nbsp;&nbsp; :&nbsp;</span></span>http://hosoduhocphap.edu.vn/</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Email</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;duhocvietphatvti@gmail.com&nbsp;</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>ĐT &nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</span></span></p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>FanPage</strong>&nbsp;&nbsp; : &nbsp;&nbsp;</span></span>https://www.facebook.com/DuhocVietPhatVTI</p>\r\n\r\n<p style="text-align:justify"><span style="font-size:14px"><span style="font-family:arial,helvetica,sans-serif"><strong>Hotline</strong>&nbsp;&nbsp; &nbsp; :&nbsp;<strong><span style="color:red">0983 102 258</span></strong></span></span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n', 1, 1169455, '2015-04-23 18:37:26', '2015-04-24 11:57:34', '/usrc/ed/8d/_t/ed8d_trong-tre-tai-phap-aupair-1.jpg', '', '', 'ho so aupair,aupair phap,chuong trinh aupair phap,vua hoc vua lam', 1, 1, 'ho so aupair,aupair phap,chuong trinh aupair phap,vua hoc vua lam', 'Việt Phát sẽ tóm tắt cho các bạn một số kinh nghiệm khi làm hồ sơ aupair Pháp - chương trình du học vừa học vừa làm để được sang Pháp sớm nhất.', 'Kinh nghiệm làm hồ sơ  chương trình Aupair Pháp - Vừa học vừa làm', 'kinh-nghiem-lam-ho-so-chuong-trinh-aupair-phap-vua-hoc-vua-lam', 4, '', '', NULL, 0, '/usrc/ed/8d/_t/thumbnail/ed8d_trong-tre-tai-phap-aupair-1.jpg'),
(223, 'Khai giảng khóa học giao tiếp từ cơ bản đến nâng cao', 'Để đáp ứng nhu cầu khác nhau của học sinh – sinh viên, Việt Phát liên tục tổ chức các khóa học giao tiếp với đầy đủ 4 kĩ năng nghe, nói, đọc, viết với đội ngũ giảng viên người bản xứ (Parisien) và thạc sĩ về phương pháp giảng dạy tại Pháp (có kinh nghiệm giảng dạy tại Pháp)', '<p style="text-align:justify">Hiện nay, thực trạng học ngoại ngữ của học sinh - sinh vi&ecirc;n n&oacute;i chung v&agrave; học sinh - sinh vi&ecirc;n Việt Nam n&oacute;i ri&ecirc;ng đ&oacute; l&agrave; tuy học ngoại ngữ đ&atilde; l&acirc;u nhưng gặp kh&oacute; khăn trong giao tiếp v&agrave; ngữ ph&aacute;p. Ch&uacute;ng ta c&oacute; thể thuộc rất nhiều từ vựng, nhưng kh&oacute; khăn trong h&igrave;nh th&agrave;nh c&acirc;u v&agrave; đặc biệt, kĩ năng nghe v&agrave; n&oacute;i rất k&eacute;m, khi gặp người bản địa ch&uacute;ng ta kh&ocirc;ng thể ph&aacute;t huy được những kiến thức m&agrave; ch&uacute;ng ta đ&atilde; học. Ch&iacute;nh v&igrave; vậy, để khắc phục những kh&oacute; khăn m&agrave; c&aacute;c bạn học sinh &ndash; sinh vi&ecirc;n gặp phải, Việt Ph&aacute;t đ&atilde; thiết kế nhiều<a href="http://hosoduhocphap.edu.vn/khai-giang-khoa-hoc-giao-tiep-tu-co-ban-den-nang-cao"><strong><span style="color:#0000FF"> chương tr&igrave;nh học giao tiếp</span></strong></a>&nbsp;đa dạng, ph&ugrave; hợp với mọi nhu cầu kh&aacute;c nhau của học sinh &ndash; sinh vi&ecirc;n với đội ngũ giảng vi&ecirc;n l&agrave; người bản địa ( Parisien) v&agrave; thạc sĩ về phương ph&aacute;p giảng dạy c&ugrave;ng kinh nghiệm giảng dạy nhiều năm tại Ph&aacute;p nhằm gi&uacute;p học sinh &ndash; sinh vi&ecirc;n tăng khả năng phản xạ, ph&aacute;t huy được kiến thức đ&atilde; học v&agrave; tự tin khi giao tiếp với người bản xứ.</p>\r\n\r\n<p style="text-align:center"><br />\r\n<br />\r\n<br />\r\n<img alt="lop-hoc-tieng-phap-tai-viet-phat" src="/usrc/f8/99/_l/f899_lop-hoc-tieng-phap-tai-viet-phat.jpg" style="margin:5px auto; position:relative; width:90%" title="Lớp học tiếng Pháp tại Việt Phát" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh lớp học tại c&ocirc;ng ty du học Việt Ph&aacute;t</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong><a href="http://hosoduhocphap.edu.vn/khai-giang-khoa-hoc-giao-tiep-tu-co-ban-den-nang-cao"><span style="color:#0000CD">Chương tr&igrave;nh học giao tiếp</span></a>&nbsp;chia l&agrave;m 3 cấp độ :</strong></p>\r\n\r\n<p style="text-align:justify"><strong>+ Sơ cấp</strong>&nbsp;: d&agrave;nh cho c&aacute;c bạn học sinh &ndash; sinh vi&ecirc;n chưa từng học tiếng Ph&aacute;p hoặc mới l&agrave;m quen với tiếng Ph&aacute;p. Sau chương tr&igrave;nh học, c&aacute;c bạn c&oacute; thể c&oacute; vốn từ vựng v&agrave; ngữ ph&aacute;p nền tảng để c&oacute; thể sử dụng, giao tiếp đơn giản với gi&aacute;o vi&ecirc;n Việt Nam hoặc gi&aacute;o vi&ecirc;n bản ngữ th&ocirc;ng qua những b&agrave;i học về ngữ ph&aacute;p v&agrave; từ vựng cơ bản.</p>\r\n\r\n<p style="text-align:justify"><strong>+ Trung cấp&nbsp;:</strong>d&agrave;nh cho c&aacute;c bạn đ&atilde; c&oacute; kiến thức nền tảng về tiếng Ph&aacute;p hay đ&atilde; qua tr&igrave;nh độ sơ cấp, c&oacute; vốn từ vựng nhất định, c&oacute; tư duy v&agrave; c&oacute; khả năng sắp xếp &yacute; tưởng, diễn đạt c&acirc;u trọn vẹn. Sau kh&oacute;a học, c&aacute;c bạn c&oacute; thể mở rộng vốn từ vựng theo c&aacute;c chủ đề, tăng t&iacute;nh biện luận &ndash; logic trong lập luận c&aacute;c quan điểm th&ocirc;ng qua c&aacute;c tr&ograve; chơi, c&aacute;c b&agrave;i học chia theo c&aacute;c chủ đề với định hướng ph&aacute;t triển tự do c&aacute;ch nh&igrave;n nhận c&aacute; nh&acirc;n trong cuộc sống h&agrave;ng ng&agrave;y như kinh tế, t&igrave;nh y&ecirc;u, x&atilde; hội&hellip;</p>\r\n\r\n<p style="text-align:justify"><strong>+ Cao cấp :&nbsp;</strong>ph&ugrave; hợp với c&aacute;c bạn đ&atilde; c&oacute; kiến thức nền tảng chắc chắn với vốn từ vựng phong ph&uacute;.</p>\r\n\r\n<p style="text-align:justify">Sau kh&oacute;a học, c&aacute;c bạn sẽ được ho&agrave;n thiện c&aacute;c kĩ năng, d&ugrave;ng từ đắt,&nbsp; diễn đạt văn phong ph&aacute;p ngữ mượt m&agrave; &ndash; tr&ocirc;i chảy với những buổi học mang t&iacute;nh chất trao đổi, đ&agrave;m luận với c&aacute;c giảng vi&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<br />\r\n<img alt="lop-hoc-ngoai-ngu" src="/usrc/cc/af/_l/ccaf_lop-hoc-ngoai-ngu.JPG" style="margin:5px auto; position:relative; width:90%" title="Lớp học ngoại ngữ" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh minh họa lớp học ngoại ngữ</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p>C&aacute;c chương tr&igrave;nh của ch&uacute;ng t&ocirc;i được thiết kế dưới 2 h&igrave;nh thức cơ bản như sau :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border="1" cellpadding="0" cellspacing="0">\r\n	<tbody>\r\n		<tr>\r\n			<td style="width:289px">\r\n			<p style="text-align:center"><strong>Lớp thường xuy&ecirc;n</strong></p>\r\n\r\n			<p style="text-align:center">Thời lượng kh&oacute;a học : 8 tuần</p>\r\n\r\n			<p style="text-align:center">Số buổi học : 3 buổi / tuần</p>\r\n\r\n			<p style="text-align:center">Thời gian mỗi buổi học : 2h</p>\r\n\r\n			<p style="text-align:center">Số giờ học mỗi tuần : 6h</p>\r\n\r\n			<p style="text-align:center">Số lượng học sinh : &lt;= 6 học sinh</p>\r\n\r\n			<p style="text-align:center">Học ph&iacute; : 1.500.000vnđ/th&aacute;ng</p>\r\n\r\n			<p style="text-align:center">&nbsp;</p>\r\n			</td>\r\n			<td style="width:289px">\r\n			<p style="text-align:center"><strong>Lớp cấp tốc</strong></p>\r\n\r\n			<p style="text-align:center">Thời lượng kh&oacute;a học : 8 tuần</p>\r\n\r\n			<p style="text-align:center">Số buổi học : 5 buổi / tuần</p>\r\n\r\n			<p style="text-align:center">Thời gian mỗi buổi học : 2h</p>\r\n\r\n			<p style="text-align:center">Số giờ học mỗi tuần : 10h</p>\r\n\r\n			<p style="text-align:center">Số lượng học sinh : &lt;= 6 học sinh</p>\r\n\r\n			<p style="text-align:center">Học ph&iacute; : 2.500.000vnđ/th&aacute;ng</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Đăng k&yacute; học</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c bạn học sinh sinh vi&ecirc;n c&oacute; thể đăng k&yacute; học theo 3 c&aacute;ch:</p>\r\n\r\n<p style="margin-left:30pt">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đăng k&yacute; theo form:&nbsp;</strong><a href="http://goo.gl/forms/Oq5sw7MZJ3"><strong>http://goo.gl/forms/Oq5sw7MZJ3</strong></a>&nbsp;</p>\r\n\r\n<p style="margin-left:30pt">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đăng k&yacute;&nbsp;trực tiếp tại văn ph&ograve;ng c&ocirc;ng ty Việt Ph&aacute;t: Số 48/ng&aacute;ch 2, Ng&otilde; SIMCO, đường Phạm H&ugrave;ng, H&agrave; Nội</strong></p>\r\n\r\n<p style="margin-left:30pt">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>Đăng k&yacute; trực tiếp&nbsp;theo số điện thoại:&nbsp;04 62 96 75 42&nbsp;-&nbsp;0983 102 258</strong></p>\r\n\r\n<p>________________________________________________________________________</p>\r\n\r\n<p><strong>Ngo&agrave;i ra, c&ocirc;ng ty tư vấn miễn ph&iacute; c&aacute;c chương tr&igrave;nh du học Ch&acirc;u &Acirc;u ph&ugrave; hợp với mọi y&ecirc;u cầu của học sinh &ndash; sinh vi&ecirc;n.</strong></p>\r\n\r\n<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t</strong></p>\r\n\r\n<p>Địa chỉ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p>Website &nbsp;&nbsp; :&nbsp;<a href="http://hosoduhocphap.edu.vn/">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p>Email &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;tuvanduhocphapvti@gmail.com</p>\r\n\r\n<p>ĐT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p>FanPage&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p><strong>Hotline &nbsp;: <span style="color:#FF0000">0983 102 258</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 98, 1169466, '2015-04-24 16:58:22', '2015-04-26 10:13:13', '/usrc/9c/18/_l/9c18_lop-hoc-tieng-phap-tai-viet-phat.jpg', '', '', 'học tiếng pháp cơ bản,tiếng pháp giao tiếp,tiếng pháp cùng người bản xứ,học tiếng pháp ở đâu hay,tieng phap co ban,tieng phap giao tiep,tieng phap cung nguoi ban xu,hoc tieng phap o dau ha noi', 1, 1, 'học tiếng pháp cơ bản, tiếng pháp giao tiếp,tiếng pháp cùng người bản xứ,học tiếng pháp ở đâu hay,  tieng phap co ban', 'Khai giảng khóa học giao tiếp từ cơ bản đến nâng cao, lớp luyện thi tcf 2015 nhằm nâng cao trình độ tiếng cho học viên, cải thiện khả năng phát âm cũng như ngữ Pháp.', 'Khai giảng khóa học giao tiếp từ cơ bản đến nâng cao', 'khai-giang-khoa-hoc-giao-tiep-tu-co-ban-den-nang-cao', 5, '', '', NULL, 0, '/usrc/9c/18/_l/thumbnail/9c18_lop-hoc-tieng-phap-tai-viet-phat.jpg'),
(224, 'Hỏi đáp về du học Pháp', 'Xin hỏi chi phí du học Pháp 2015 thì có hết nhiều không? Em xin cảm ơn ạ!', '<p>Ch&agrave;o bạn!</p>\r\n\r\n<p>Ph&aacute;p l&agrave; một trong v&agrave;i nước c&oacute; chi ph&iacute; rất rẻ. Nếu bạn học trường c&ocirc;ng th&igrave; gần như kh&ocirc;ng mất tiền học ph&iacute;, chỉ mất chi ph&iacute; sinh hoạt. &nbsp;M&agrave; chi ph&iacute; tiền nh&agrave; c&ograve;n được nh&agrave; nước hỗ trợ &iacute;t nhất 60%.</p>\r\n\r\n<p>Em c&oacute; thể cho chị số điện thoại, chị c&oacute; thể gọi điện cho em, lắng nghe nguyện vọng của em v&agrave; tư vấn miễn ph&iacute; cho em kh&ocirc;ng?</p>\r\n\r\n<p>Chị cảm ơn em nhiều nh&eacute; !</p>\r\n', 0, 1169450, '2015-04-25 09:10:51', '2015-04-25 17:03:46', '/usrc/2c/d9/_h/2cd9_hoi%20dap.jpg', '', '', '', 0, 1, 'du hoc phap, thắc mắc du học  pháp,thắc mắc du học 2015', 'Giải đáp những thắc mắc của các bạn học sinh về việc du học Pháp trong năm 2015 này, đảm bảo thành công trên con đường du học của mình.', 'Thắc mắc về du học Pháp 2015', 'hoi-dap-ve-du-hoc-phap', 1000, 'Nguyễn Quỳnh Thi', 'bethichcatinh1ht95@gmail.com', NULL, 0, '/usrc/2c/d9/_h/thumbnail/2cd9_hoi%20dap.jpg');
INSERT INTO `sa_entry` (`id`, `title`, `description`, `content`, `creator_id`, `category_id`, `create_time`, `update_time`, `image_path`, `facebook_url`, `google_url`, `tags`, `is_top`, `display`, `seo_keyword`, `seo_description`, `seo_title`, `rewrite_url`, `order_number`, `asker`, `asker_email`, `category_title`, `total_views`, `thumbnail_url`) VALUES
(225, 'Kinh nghiệm phỏng vấn campusfrance', 'Ở thời điểm hiện tại, đã rất nhiều bạn đã trải qua phỏng vấn với campus rồi, nhưng bên cạnh đó vẫn còn rất nhiều bạn vẫn chưa trải qua, có phải đang rất lo lắng không? Bởi làm hồ sơ quan trọng, nhưng phỏng vấn campus quan trọng không kém vì dường như nó là yếu tố quyết định đến khả năng và tương lai du học Pháp của bạn. Vậy Việt Phát sẽ giúp đỡ các bạn cảm thấy bớt căng thẳng và lo lắng, Việt Phát sẽ hướng dẫn các bạn trả lời một số câu hỏi trọng tâm không thể thiếu trong quá trình phỏng vấn với campus.', '<p style="text-align:justify"><strong>Tại sao bạn lại chọn trường đại học n&agrave;y v&agrave; bạn đ&atilde; t&igrave;m thấy n&oacute; như thế n&agrave;o?</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Để trả lời tốt cho c&acirc;u hỏi n&agrave;y, bạn phải t&igrave;m hiểu trước th&ocirc;ng tin c&aacute;c trường m&agrave; bạn chọn th&ocirc;ng qua trang web của trường. Đặc biệt, nếu c&oacute; thể bạn n&ecirc;n xem xếp hạng của tr&ecirc;n tr&ecirc;n thế giới n&oacute;i chung hoặc của Ph&aacute;p n&oacute;i ri&ecirc;ng, c&aacute;c cơ sở nghi&ecirc;n cứu hay đội ngũ gi&aacute;o vi&ecirc;n của trường v.v&hellip; Tất cả những th&ocirc;ng tin n&agrave;y thể hiện bạn đ&atilde; c&oacute; hiểu biết về trường v&agrave; tạo được ấn tượng tốt với người phỏng vấn cơ sở m&agrave; bạn chọn trường Đại học n&agrave;y.</p>\r\n\r\n<p style="text-align:justify">Việt Ph&aacute;t đưa ra một gợi &yacute; trả lời cho c&acirc;u hỏi tr&ecirc;n như sau : &ldquo; Qua campus t&ocirc;i được biết đến trường Đại học n&agrave;y, sau đ&oacute; t&ocirc;i đ&atilde; truy cập v&agrave;o trang web ch&iacute;nh thức của trường để t&igrave;m hiểu th&ecirc;m th&ocirc;ng tin. Th&ocirc;ng qua t&igrave;m hiểu, t&ocirc;i được biết đ&acirc;y l&agrave; một ng&ocirc;i trường uy t&iacute;n v&agrave; được đ&aacute;nh gi&aacute; &ndash; xếp hạng cao trong lĩnh vực m&agrave; t&ocirc;i chọn. N&oacute; gần như giống với tất cả mọi nguyện vọng của bản th&acirc;n t&ocirc;i n&ecirc;n t&ocirc;i đ&atilde; thực sự ấn tượng v&agrave; mong muốn được học tập tại ng&ocirc;i trường n&agrave;y&rdquo;.</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Minh họa phỏng vấn" src="/usrc/4a/19/_8/4a19_83141ae1e222d3138a994dcac3627ac6_116.jpg" style="margin:5px auto; position:relative; width:90%" title="Minh họa phỏng vấn " /></p>\r\n\r\n<p style="text-align:center">Minh họa về phỏng vấn</p>\r\n\r\n<p style="text-align:justify"><strong>Tại sao bạn lại chọn<a href="http://duhocvietphat.edu.vn/2-du-hoc-phap">&nbsp;đi du học ở Ph&aacute;p</a>&nbsp;&nbsp;m&agrave; kh&ocirc;ng phải ở một nước kh&aacute;c?</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Để trả lời c&acirc;u hỏi n&agrave;y, bạn n&ecirc;n nhấn mạnh đến chất lượng gi&aacute;o dục ở Ph&aacute;p. Bạn c&oacute; thể liệt k&ecirc; đơn giản những thế mạnh, sự kh&aacute;c biệt, chất lượng, gi&aacute; trị bằng cấp tr&ecirc;n thế giới, ch&iacute;nh s&aacute;ch &ndash; m&ocirc;i trường gi&aacute;o dục của Ph&aacute;p&hellip;Nếu bạn chọn một chuy&ecirc;n ng&agrave;nh tại Ph&aacute;p m&agrave; ở Việt Nam cũng đ&agrave;o tạo, bạn n&ecirc;n n&oacute;i những lợi &iacute;ch bạn sẽ đạt được nếu học ở Ph&aacute;p thay v&igrave; học ở Việt Nam. C&ograve;n nếu bạn chọn một chuy&ecirc;n ng&agrave;nh m&agrave; ở Ph&aacute;p đ&agrave;o tạo nhưng ở Việt Nam kh&ocirc;ng c&oacute;, bạn phải tr&igrave;nh b&agrave;y r&otilde; r&agrave;ng bạn lựa chọn chuy&ecirc;n ng&agrave;nh đ&oacute;, tại sao?</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Bạn c&oacute; dự định l&agrave;m việc ở đất nước ch&uacute;ng t&ocirc;i sau khi kh&oacute;a học kết th&uacute;c?</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Mục đ&iacute;ch của người phỏng vấn ở c&acirc;u hỏi n&agrave;y đ&oacute; l&agrave; muốn biết bạn c&oacute; sẵn s&agrave;ng quay trở về nước sau khi học xong hay kh&ocirc;ng? Đ&acirc;y ch&iacute;nh l&agrave; l&yacute; do rất phổ biến khiến nhiều sinh vi&ecirc;n bị từ chối cấp visa v&igrave; trong qu&aacute; tr&igrave;nh phỏng vấn, bản th&acirc;n sinh vi&ecirc;n kh&ocirc;ng chứng minh được động lực trở về nước sau khi ho&agrave;n tất qu&aacute; tr&igrave;nh học.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Vậy để th&agrave;nh c&ocirc;ng trong c&acirc;u hỏi n&agrave;y, bạn phải lu&ocirc;n suy nghĩ v&agrave; chuẩn bị tinh thần &ndash; động lực trở về v&igrave; nh&acirc;n vi&ecirc;n Campus sẽ hỏi bạn những c&acirc;u hỏi mang t&iacute;nh chất x&aacute;c định động lực trở về nước của bạn th&ocirc;ng qua kế hoạch học tập v&agrave; r&agrave;ng buộc của bạn với nước Ph&aacute;p. Ch&iacute;nh v&igrave; vậy, chuẩn bị c&acirc;u hỏi, kiến thức, hồ sơ để phỏng vấn cực k&igrave; quan trọng nhằm tạo ấn tượng về con người của m&igrave;nh cũng như quyết t&acirc;m &ndash; nguyện vọng học tập tại Ph&aacute;p.</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Minh họa câu hỏi" src="/usrc/d4/73/_c/d473_ce2d89e9d8a6b4e5da55d337e36f5f20_116.jpg" style="margin:5px auto; position:relative; width:90%" title="Minh họa các câu hỏi phỏng vấn" /></p>\r\n\r\n<p style="text-align:center">Minh họa về c&aacute;c c&acirc;u hỏi</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bạn sẽ ở đ&acirc;u khi du học tại đất nước t&ocirc;i?</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng thường, bạn cần tiến h&agrave;nh việc t&igrave;m chỗ ở trước khi đi &nbsp;phỏng vấn&nbsp;v&agrave; n&ecirc;n ghi ch&eacute;p cẩn thận, hoặc tốt nhất l&agrave; nhớ ch&iacute;nh x&aacute;c địa chỉ nơi đến. Điều đ&oacute; sẽ chứng tỏ bạn c&oacute; t&igrave;m hiểu v&agrave; l&ecirc;n kế hoạch cho việc&nbsp;<strong><a href="http://duhocvietphat.edu.vn/2-du-hoc-phap">du học Ph&aacute;p</a></strong>&nbsp;một c&aacute;ch nghi&ecirc;m t&uacute;c, kỹ lưỡng.</p>\r\n\r\n<p>Hoặc nếu bạn tiến h&agrave;nh phỏng vấn trước khi c&oacute; trường n&agrave;o đ&oacute; nhận, bạn c&oacute; thể trả lời rằng bạn đ&atilde; nộp hồ sơ v&agrave;o một số trường Đại học, c&ograve;n phụ thuộc v&agrave;o trường, sau đ&oacute; b&ecirc;n cạnh bạn sẽ nhờ sự gi&uacute;p đỡ của hội sinh vi&ecirc;n Việt Nam tại th&agrave;nh phố đ&oacute; hỗ trợ t&igrave;m nh&agrave; hoặc bạn sẽ trực tiếp li&ecirc;n hệ với chủ nh&agrave; qua một số trang web uy t&iacute;n v&agrave; nổi tiếng như leboncoin&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Người t&agrave;i trợ cho việc học của bạn l&agrave; ai? Chi ph&iacute; cho việc học của bạn l&agrave; bao nhi&ecirc;u?</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y l&agrave; c&acirc;u hỏi rất quan trọng nhằm kiềm tra v&agrave; x&aacute;c minh nguồn t&agrave;i ch&iacute;nh cho qu&aacute; tr&igrave;nh bạn học tập tại Ph&aacute;p. Ch&iacute;nh v&igrave; vậy, th&ocirc;ng tin v&agrave; giấy tờ chứng minh t&agrave;i ch&iacute;nh phải tr&ugrave;ng khớp với qu&aacute; tr&igrave;nh khai hồ sơ. Nếu du học tự t&uacute;c, bạn h&atilde;y tr&igrave;nh b&agrave;y r&otilde; nghề nghiệp &ndash; chức vụ bố mẹ, cơ quan l&agrave;m việc, thu nhập h&agrave;ng th&aacute;ng&hellip;c&ugrave;ng với giấy chứng m&igrave;nh t&agrave;i ch&iacute;nh ( một khoản tiền trong t&agrave;i khoản ng&acirc;n h&agrave;ng ). Nếu l&agrave; học bổng, bạn xin giấy x&aacute;c nhận mức học bổng của tổ chức để l&agrave;m giấy tờ chứng minh t&agrave;i ch&iacute;nh.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Bạn c&oacute; kế hoạch g&igrave; khi học xong?</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mục đ&iacute;ch của người phỏng vấn ở c&acirc;u hỏi n&agrave;y l&agrave; muốn biết kế hoạch tương lai của bạn, nhưng s&acirc;u xa hơn l&agrave; muốn kiểm tra liệu bạn c&oacute; nghi&ecirc;m t&uacute;c với ng&agrave;nh học m&agrave; m&igrave;nh theo đuổi. Bạn n&ecirc;n thể hiện r&otilde; điều n&agrave;y. Bởi vậy, h&atilde;y tr&igrave;nh b&agrave;y cụ thể v&agrave; r&otilde; r&agrave;ng về kế hoạch của m&igrave;nh. V&iacute; dụ: &ldquo;Khi học xong ng&agrave;nh kinh tế, t&ocirc;i sẽ trở về nước, xin v&agrave;o l&agrave;m ở một c&ocirc;ng ty c&oacute; nhiều kinh nghiệm trong lĩnh vực n&agrave;y. Nếu c&oacute; điều kiện, t&ocirc;i muốn mở một c&ocirc;ng ty ri&ecirc;ng khi đ&atilde; t&iacute;ch lũy đủ vốn v&agrave; kinh nghiệm&hellip;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<br />\r\n<img alt="Minh họa phỏng vấn " src="/usrc/cc/e2/_c/cce2_cf33ae8a50e6c902ec8c2e8a89ec1c1e_116.jpg" style="margin:5px auto; position:relative; width:90%" title="Minh họa phỏng vấn" /></p>\r\n\r\n<p style="text-align:center">Minh họa phỏng vấn</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Bạn c&oacute; người th&acirc;n n&agrave;o ở nước ch&uacute;ng t&ocirc;i kh&ocirc;ng?</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">H&atilde;y trung thực v&agrave; kh&ocirc;n kh&eacute;o khi gặp c&acirc;u hỏi n&agrave;y. Nếu bạn c&oacute; người th&acirc;n ở nước đ&oacute; nhưng kh&aacute; xa nơi bạn theo học hoặc họ h&agrave;ng xa, c&oacute; thể kh&ocirc;ng cần đề cập nhiều về họ. Nếu bạn c&oacute; họ h&agrave;ng lưu tr&uacute; bất hợp ph&aacute;p hay qu&aacute; hạn visa ở nước đ&oacute; th&igrave; đ&acirc;y sẽ l&agrave; một bất lợi, kh&ocirc;ng n&ecirc;n đề cập đến.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><span style="color:#800080"><em><strong>Ngo&agrave;i ra, một yếu tố kh&aacute;c rất quan trọng trong qu&aacute; tr&igrave;nh phỏng vấn đ&oacute; l&agrave; th&aacute;i độ v&agrave; t&aacute;c phong khi phỏng vấn.</strong></em></span></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Khi đi phỏng vấn c&aacute;c bạn n&ecirc;n mặc những bộ trang phục nghi&ecirc;m t&uacute;c, đơn giản, lịch sự, bạn n&ecirc;n để đầu t&oacute;c gọn g&agrave;ng v&agrave; tuyệt đối kh&ocirc;ng được đi d&eacute;p l&ecirc; nh&eacute;.</p>\r\n\r\n<p style="text-align:justify">H&ocirc;m phỏng vấn c&aacute;c bạn n&ecirc;n đến sớm trước giờ phỏng vấn từ 10 đến 20 ph&uacute;t để c&oacute; thời gian chuẩn bị v&agrave; ổn định t&acirc;m l&yacute;, đồng thời bạn c&oacute; thể tham khảo xem c&aacute;c bạn phỏng vấn trước m&igrave;nh phỏng vấn thế n&agrave;o v&agrave; th&aacute;i độ của người phỏng vấn.</p>\r\n\r\n<p style="text-align:justify">Bước v&agrave;o ph&ograve;ng phỏng vấn c&aacute;c bạn n&ecirc;n b&igrave;nh tĩnh v&agrave; h&iacute;t thở thật s&acirc;u để lấy lại b&igrave;nh tĩnh, đầu ti&ecirc;n bạn h&atilde;y ch&agrave;o người phỏng vấn khi bước v&agrave;o ph&ograve;ng &ldquo;Bonjour Madame/Monsieur&rdquo;. Khi phỏng vấn tập trung v&agrave;o c&acirc;u hỏi của người phỏng vấn, mắt nh&igrave;n thẳng v&agrave;o người phỏng vấn, tươi cười ch&agrave;o hỏi. Nếu c&oacute; những c&acirc;u hỏi bạn nghe kh&ocirc;ng r&otilde; hoặc kh&ocirc;ng hiểu bạn h&atilde;y đề nghị người phỏng vấn nhắc lại hoặc hỏi theo một &nbsp;c&aacute;ch kh&aacute;c v&agrave; nhớ l&agrave; bao giờ cũng phải c&oacute; c&acirc;u &ldquo;s&rsquo;il vous plait&rdquo; ở cuối c&acirc;u để thể hiện sự lịch sự v&agrave; t&ocirc;n trọng nh&acirc;n vi&ecirc;n phỏng vấn Campus France. C&aacute;c bạn n&ecirc;n lưu &yacute; l&agrave; v&igrave; đi &nbsp;<strong><a href="http://duhocvietphat.edu.vn/2-du-hoc-phap">du học Ph&aacute;p</a></strong>&nbsp;sẽ phỏng vấn bằng tiếng Ph&aacute;p n&ecirc;n bạn đừng n&ecirc;n n&oacute;i c&acirc;u tiếng Việt n&agrave;o trong l&uacute;c phỏng vấn. Kết th&uacute;c buổi phỏng vấn bạn h&atilde;y n&oacute;i c&aacute;m ơn v&agrave; tạm biệt người phỏng vấn khi kết th&uacute;c. Đ&oacute; l&agrave; những ph&eacute;p lịch sự tối thiểu cần phải c&oacute; để tạo n&ecirc;n ấn tượng tốt với người phỏng vấn bạn.</p>\r\n\r\n<p style="text-align:justify">Sau buổi phỏng vấn, c&aacute;c bạn sẽ được cấp 1 giấy chứng nhận đ&atilde; qua phỏng vấn với campus. Đ&acirc;y l&agrave; giấy chứng nhận kh&ocirc;ng thể thiếu khi đi xin visa sang Ph&aacute;p.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Tr&ecirc;n đ&acirc;y l&agrave; những<a href="http://hosoduhocphap.edu.vn/kinh-nghiem-phong-van-campusfrance">&nbsp;</a><a href="http://hosoduhocphap.edu.vn/kinh-nghiem-phong-van-campusfrance"><strong>kinh nghiệm phỏng vấn Campusfrance</strong></a>&nbsp;hy vọng g&oacute;p phần gi&uacute;p đỡ c&aacute;c bạn trong &nbsp;khi phỏng vấn v&agrave; gi&uacute;p bạn tự tin hơn để c&oacute; được kết quả tốt&nbsp;..&nbsp;Nếu c&aacute;c bạn c&oacute; nhiều c&acirc;u hỏi hơn nữa về c&aacute;c dạng c&acirc;u hỏi phỏng vấn hoặc muốn tư vấn miễn ph&iacute; về c&aacute;c chương tr&igrave;nh du học th&igrave; h&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i nh&eacute;.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div>\r\n<hr /></div>\r\n\r\n<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t - VTI</strong></p>\r\n\r\n<p><strong>Địa chỉ&nbsp;</strong>&nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;&nbsp;Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p><strong>Website</strong>&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;<a href="http://hosoduhocphap.edu.vn/">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p><strong>Email</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;tuvanduhocphapvti@gmail.com&nbsp;</p>\r\n\r\n<p><strong>ĐT &nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p><strong>FanPage</strong>&nbsp;&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p><strong>Hotline</strong>&nbsp;&nbsp; &nbsp; :&nbsp;<strong>0983 102 258</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 98, 1169461, '2015-04-25 10:17:58', '2015-05-05 13:17:00', '/usrc/45/dc/_8/45dc_8614babc394ff48f1428174bcefcc0b9_116%20%281%29.gif', '', '', 'kinh nghiệm phỏng vấn campus,câu hỏi phỏng vấn campus,phỏng vấn campus,phỏng vấn đại sứ quán,kinh nghiem phong van campus,cau hoi phong van campus,phong van campus', 0, 1, 'kinh nghiệm phỏng vấn campus,câu hỏi phỏng vấn campus,phỏng vấn campus,phỏng vấn đại sứ quán,kinh nghiem phong van campus,cau hoi phong van campus,phong van campus', 'Để đi du học Pháp thành công thì trước hết bạn phải pass được vòng phỏng vấn. Kinh nghiệm phỏng vấn campusfrance sẽ giúp các bạn làm được vấn đề này.', 'Kinh nghiệm phỏng vấn campusfrance', 'kinh-nghiem-phong-van-campusfrance', 1001, '', '', NULL, 0, '/usrc/45/dc/_8/thumbnail/45dc_8614babc394ff48f1428174bcefcc0b9_116%20%281%29.gif'),
(226, 'Đại học Tổng hợp Montpellier 1', 'Đại học  Montpellier 1 có  bề dày lịch sử lịch sử lâu đời trong hệ thống các trường đại học tổng hợp tại Pháp.Tiền thân là trường Đại học Montpellier ra đời năm 1289, nhưng năm 1969, trường đại học Pháp hiện đại như ngày nay được thành lập.Trụ sở chính  của trường nằm ở thành phố Montpellier xinh đẹp của nước Pháp. Hiện nay trường là trường đại học lớn nhất ở thành phố Montpellier thuộc miền Nam nước Pháp  với tổng số  khoảng 21000 sinh viên theo học các lĩnh vực chính như Kinh tế quản lý và khoa học sức khỏe.', '<p style="text-align:justify">Đại học&nbsp; Montpellier 1 c&oacute;&nbsp; bề d&agrave;y lịch sử lịch sử l&acirc;u đời trong hệ thống c&aacute;c trường<strong><span style="color:#0000CD"> <a href="http://hosoduhocphap.edu.vn/dai-hoc-tong-hop-montpellier-1">đại học tổng hợp tại Ph&aacute;p</a></span></strong>.Tiền th&acirc;n l&agrave; trường Đại học Montpellier ra đời năm 1289, nhưng năm 1969, trường<strong><span style="color:#0000CD"> </span></strong>đại học Ph&aacute;p<strong><span style="color:#0000CD"> </span></strong>hiện đại như ng&agrave;y nay được th&agrave;nh lập. Trụ sở ch&iacute;nh&nbsp; của trường nằm ở th&agrave;nh phố Montpellier xinh đẹp của nước Ph&aacute;p. Hiện nay trường l&agrave; trường đại học lớn nhất ở th&agrave;nh phố Montpellier thuộc miền Nam nước Ph&aacute;p&nbsp; với tổng số&nbsp; khoảng 21000 sinh vi&ecirc;n theo học c&aacute;c lĩnh vực ch&iacute;nh như Kinh tế quản l&yacute; v&agrave; khoa học sức khỏe.</p>\r\n\r\n<p style="text-align:center"><br />\r\n<br />\r\n<img alt="Thanh-pho-Montpellier" src="/usrc/02/33/_t/0233_th%C3%A0nh%20ph%E1%BB%91%20Montpellier.jpg" style="margin:5px auto; position:relative; width:90%" title="Thành phố Montpellier" /></p>\r\n\r\n<p style="text-align:center">Th&agrave;nh phố Montpellier</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Theo m&ocirc; h&igrave;nh của c&aacute;c <a href="http://hosoduhocphap.edu.vn/dai-hoc-tong-hop-montpellier-1"><span style="color:#0000CD"><strong>trường đại học tổng hợp tại Ph&aacute;p</strong></span></a>, trường&nbsp; th&agrave;nh&nbsp; lập c&aacute;c Đơn vị đ&agrave;o tạo v&agrave;&nbsp; nghi&ecirc;n cứu (UFR) với c&aacute;c viện, khoa kh&aacute;c nhau bao gồm bẩy UFR trực thuộc:</p>\r\n\r\n<p style="text-align:justify">- Khoa Luật v&agrave;&nbsp; Khoa học ch&iacute;nh trị thuộc quản l&yacute; kinh tế</p>\r\n\r\n<p style="text-align:justify">- UFR Kinh tế</p>\r\n\r\n<p style="text-align:justify">- UFR Quản l&yacute; Kinh tế &amp; X&atilde; hội (AES)</p>\r\n\r\n<p style="text-align:justify">- UFR Y khoa</p>\r\n\r\n<p style="text-align:justify">- UFR Dược khoa</p>\r\n\r\n<p style="text-align:justify">- UFR Nha khoa</p>\r\n\r\n<p style="text-align:justify">- UFR ứng dụng khoa học kĩ thuật trong c&aacute;c hoạt động thể dục thể thao</p>\r\n\r\n<p style="text-align:justify">&nbsp;Hai&nbsp; viện thuộc trường:</p>\r\n\r\n<p style="text-align:justify">- Viện dự bị h&agrave;nh ch&iacute;nh c&ocirc;ng</p>\r\n\r\n<p style="text-align:justify">- Viện Khoa học về Doanh nghiệp v&agrave; &nbsp;Quản trị</p>\r\n\r\n<p style="text-align:center"><br />\r\n<br />\r\n<img alt="Giang-duong-truong-dai-hoc-Montpellier" src="/usrc/1b/16/_g/1b16_gi%E1%BA%A3ng%20%C4%91%C6%B0%E1%BB%9Dng%20tr%C6%B0%E1%BB%9Dng%20%C4%90H%20Montpellier.jpg" style="margin:5px auto; position:relative; width:90%" title="Giảng đường trường Đại học Montpellier" /></p>\r\n\r\n<p style="text-align:center">Giảng đường trường Đại học Montpellier</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Trường Montpellier 1 đ&agrave;o tạo theo ti&ecirc;u chuẩn chung ch&acirc;u &Acirc;u&nbsp; LMD (Licence-Master-Doctorat). Hệ Cử nh&acirc;n (Licence) chia th&agrave;nh licence g&eacute;n&eacute;rale (cử nh&acirc;n cơ bản) v&agrave;&nbsp; Licence pro (cử nh&acirc;n thực h&agrave;nh), tương tự với bằng Thạc sỹ (Master) chia th&agrave;nh Master professionel ( thạc sỹ chuy&ecirc;n ng&agrave;nh &nbsp;v&agrave;&nbsp; Master recherche (thạc sỹ nghi&ecirc;n cứu). Trường đ&agrave;o tạo hệ Tiến sỹ (Doctorat) chia theo 4 ng&agrave;nh:</p>\r\n\r\n<p style="text-align:justify">- Tiến sỹ Luật &amp; Ch&iacute;nh trị</p>\r\n\r\n<p style="text-align:justify">- Tiến sỹ Kinh tế &amp; Quản l&yacute;</p>\r\n\r\n<p style="text-align:justify">- Tiến sỹ Sinh học &ndash; Y tế</p>\r\n\r\n<p style="text-align:justify">- Tiến sỹ Khoa học về nh&acirc;n ch&uacute;ng học nghi&ecirc;n cứu về sự tiến ho&aacute; của lo&agrave;i người.</p>\r\n\r\n<p style="text-align:justify">Ngo&agrave;i ra l&agrave; c&aacute;c hệ ngo&agrave;i chương tr&igrave;nh LMD &nbsp;như c&aacute;c bằng đại học chuy&ecirc;n ng&agrave;nh trong &nbsp;lĩnh vực Y tế, Dược, bằng Nha khoa,&hellip;</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><em><strong>Để c&oacute; th&ocirc;ng tin chi tiết về c&aacute;c trường đại học tại Ph&aacute;p đặc biệt về trường đại học Monpellier 1, &nbsp;xin vui l&ograve;ng li&ecirc;n hệ :</strong></em></p>\r\n\r\n<p style="text-align:justify"><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t - VTI</strong></p>\r\n\r\n<p style="text-align:justify"><strong>Địa chỉ&nbsp;</strong>&nbsp; &nbsp; &nbsp;:&nbsp;&nbsp;&nbsp;Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p style="text-align:justify"><strong>Website</strong>&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;<a href="http://hosoduhocphap.edu.vn/">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p style="text-align:justify"><strong>Email</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;tuvanduhocphapvti@gmail.com&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>ĐT &nbsp;</strong>&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p style="text-align:justify"><strong>FanPage</strong>&nbsp;&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p style="text-align:justify"><strong>Hotline</strong>&nbsp;&nbsp; &nbsp; :&nbsp;<span style="color:#FF0000"><strong>0983 102 258</strong></span></p>\r\n', 98, 1169448, '2015-04-25 10:59:29', '2015-05-06 09:06:53', '/usrc/b7/84/_^/b784_%5E20A17740A489BEA2B13B1443E6A3B450C8DA6915D52F93997E%5Epimgpsh_fullsize_distr.jpg', '', '', 'dai hoc tong hop,dai hoc montpellier 1,dai hoc tong hop montpellier', 0, 1, 'dai hoc tong hop,dai hoc montpellier 1,dai hoc tong hop montpellier', 'Đại học  Montpellier 1 có  bề dày lịch sử lịch sử lâu đời trong hệ thống các trường đại học tổng hợp tại Pháp - Là trường đại học hàng đầu cho bạn có mong muốn du học.', 'Đại học Tổng hợp Montpellier 1', 'dai-hoc-tong-hop-montpellier-1', 1002, '', '', NULL, 0, '/usrc/b7/84/_^/thumbnail/b784_%5E20A17740A489BEA2B13B1443E6A3B450C8DA6915D52F93997E%5Epimgpsh_fullsize_distr.jpg'),
(227, 'Hệ thống giáo dục của nước Pháp', 'Hệ thống giáo dục của Pháp phần lớn tương đồng với hệ thống giáo dục ở Việt Nam. Tuy nhiên vẫn có những sự khác biệt. Để hiểu rõ hơn về hệ thống giáo dục Pháp , Việt Phát khái quát hệ thống giáo dục Pháp cho các bạn  yêu thích, có nhu cầu và quan tâm đến nền giáo dục Pháp.', '<p><strong>Hệ thống gi&aacute;o dục của Ph&aacute;p cũng được chia ra ba cấp:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Gi&aacute;o Dục Tiểu Học</p>\r\n\r\n<p>- Gi&aacute;o Dục Trung Học</p>\r\n\r\n<p>- Gi&aacute;o Dục Đại Học</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Trường tiểu học ở Pháp" src="/usrc/03/50/_t/0350_truong%20tieu%20hoc.jpg" style="margin:5px auto; position:relative; width:90%" title="Trường Tiểu học ở Pháp" /></p>\r\n\r\n<p style="text-align:center">Lớp học trong trường tiểu học ở Ph&aacute;p</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Ở Ph&aacute;p, c&oacute; ba loại văng bằng được c&aacute;c nước tr&ecirc;n thế giới n&oacute;i chung v&agrave; cộng đồng Ch&acirc;u &Acirc;u n&oacute;i ri&ecirc;ng c&ocirc;ng nhận. Đ&oacute; l&agrave; :</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">- Bằng cử nh&acirc;n ( Licence hay Licence professionnel)</p>\r\n\r\n<p style="text-align: justify;">- Bằng Thạc Sĩ (Master)</p>\r\n\r\n<p style="text-align: justify;">- Tiến Sĩ (Doctorat)</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Trong <a href="http://hosoduhocphap.edu.vn/he-thong-giao-duc-cua-nuoc-phap"><strong>hệ thống gi&aacute;o dục của Ph&aacute;p</strong></a>, tất cả c&aacute;c chương tr&igrave;nh gi&aacute;o dục ở Ph&aacute;p đều do Bộ gi&aacute;o dục điều động v&agrave; quản l&yacute;. Đứng đầu Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o Tạo l&agrave; Bộ Trưởng. Gi&aacute;o vi&ecirc;n v&agrave; gi&aacute;o sư trong hệ thống gi&aacute;o dục Ph&aacute;p l&agrave; những c&ocirc;ng chức. Ngay cả gi&aacute;o sư đại học v&agrave; những nh&agrave; nghi&ecirc;n cứu đều được trả lương bởi nh&agrave; nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Trường trung học cơ sở ở Pháp" src="/usrc/9f/fd/_t/9ffd_truong%20trung%20hoc%20co%20so.jpg" style="margin:5px auto; position:relative; width:90%" title="Trường Trung học cơ sở ở Pháp" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh minh họa trường Trung học cơ sở ở Ph&aacute;p</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Ở cấp tiểu học v&agrave; trung học, Ph&aacute;p sử dụng một chương tr&igrave;nh gi&aacute;o dục đồng đều cho mọi học sinh, mọi cấp lớp kh&aacute;c nhau. Chương tr&igrave;nh gi&aacute;o dục n&agrave;y được &aacute;p dụng cho c&aacute;c trường c&ocirc;ng lập, tư thục hay c&aacute;c cơ sở gi&aacute;o dục được cấp ng&acirc;n s&aacute;ch ho&agrave;n to&agrave;n của nh&agrave; nước. C&aacute;c trường học khai giảng v&agrave;o th&aacute;ng 9 v&agrave; nghỉ h&egrave; v&agrave;o th&aacute;ng 5. V&agrave; học sinh &ndash; sinh vi&ecirc;n ở Ph&aacute;p, một năm c&oacute; 5 k&igrave; nghỉ ch&iacute;nh như sau :</p>\r\n\r\n<p style="text-align: justify;">+ Nghỉ lễ c&aacute;c th&aacute;nh ( La Toussaint ) : được nghỉ khoảng 2 tuần bắt đầu v&agrave;o cuối th&aacute;ng 10.</p>\r\n\r\n<p style="text-align: justify;">+ Nghỉ Gi&aacute;ng sinh v&agrave; Năm mới : 2 tuần.</p>\r\n\r\n<p style="text-align: justify;">+ Nghỉ đ&ocirc;ng ( Hiver ) : 2 tuần bắt đầu từ giữa th&aacute;ng 2</p>\r\n\r\n<p style="text-align: justify;">+ Nghỉ xu&acirc;n ( Printemps ) : 2 tuần bắt đầu từ giữa th&aacute;ng 4</p>\r\n\r\n<p style="text-align: justify;">+ Nghỉ h&egrave; ( Ete ) : 2 -3 th&aacute;ng bắt đầu từ th&aacute;ng 6.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Bậc Tiểu Học</strong></p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Bậc tiểu học ở Ph&aacute;p bắt đầu từ năm 6 tuổi. Đ&acirc;y l&agrave; năm c&aacute;c em sẽ được học những kĩ năng cơ bản trong đọc v&agrave; viết. Giống như ở Việt Nam, bậc tiểu học chỉ c&oacute; 1-2 thầy c&ocirc; đứng lớp v&agrave; dạy to&agrave;n bộ c&aacute;c m&ocirc;n học.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Bậc Trung Học cơ sở ( coll&egrave;ge )</strong></p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Để tiếp tục học l&ecirc;n Trung học phổ th&ocirc;ng, từ năm 2007, sẽ x&eacute;t điểm học bạ từ năm thứ 3 của trung học cơ sở v&agrave; c&oacute; k&igrave; thi s&aacute;t hạch cho học sinh bao gồm c&aacute;c m&ocirc;n ph&aacute;p văn, lịch sử, địa l&yacute;, gi&aacute;o dục c&ocirc;ng d&acirc;n. Nhưng bắt đầu từ năm 2011, học sinh phải trải qua s&aacute;t hạch th&ecirc;m m&ocirc;n lịch sử của nghệ thuật.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Bằng Trung Học phổ th&ocirc;ng (Baccalaur&eacute;at). </strong></p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Bằng n&agrave;y học sinh sẽ được nhận sau 12 năm học sau khi trải qua k&igrave; thi tốt nghiệp, ở tuổi 18 nếu học sinh đ&oacute; chưa từng bị ở lại lớp ( chưa từng bị đ&uacute;p ).</p>\r\n\r\n<p style="text-align: justify;">K&igrave; thi tốt nghiệp ( gọi tắt l&agrave; Bac ) l&agrave; k&igrave; thi gồm 3 ban : Ban Khoa học tập trung v&agrave;o c&aacute;c ng&agrave;nh khoa học tự nhi&ecirc;n, khoa học vật l&iacute; v&agrave; to&aacute;n học. Ban Kinh tế v&agrave; X&atilde; hội tập trung v&agrave;o kinh tế học, khoa học x&atilde; hội v&agrave; to&aacute;n. Ban văn chương tập trung v&agrave;o Ph&aacute;p văn, ngoại ngữ, triết học, sử địa v&agrave; nghệ thuật. Mặc d&ugrave; học sinh thi 3 ng&agrave;nh kh&aacute;c nhau nhưng kh&ocirc;ng hạn chế chọn lựa chuy&ecirc;n ng&agrave;nh khi l&ecirc;n Đại học.&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Ngo&agrave;i ra c&ograve;n c&oacute; bằng t&uacute; t&agrave;i kĩ thuật v&agrave; t&uacute; t&agrave;i chuy&ecirc;n m&ocirc;n. Bằng t&uacute; t&agrave;i kĩ thuật đ&agrave;o tạo về thực h&agrave;nh v&agrave; l&yacute; thuyết về ng&agrave;nh nghề để sinh vi&ecirc;n c&oacute; thể học l&ecirc;n cao hơn. C&ograve;n bằng t&uacute; t&agrave;i chuy&ecirc;n m&ocirc;n đ&agrave;o tạo về kiến thức để học sinh đi theo v&agrave;o thị trường lao động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Trường Trung học Phổ Thông ở Pháp" src="/usrc/a2/03/_t/a203_truong%20trung%20hoc%20pho%20thong.jpg" style="margin:5px auto; position:relative; width:90%" title="Trường Trung học Phổ thông ở Pháp" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh minh họa một trường Trung học Phổ Th&ocirc;ng ở Ph&aacute;p</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Gi&aacute;o Dục Bậc Đại Học.</strong></p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">&nbsp;Gi&aacute;o dục bậc đại học được chia ra ba cấp bậc :</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">- Bằng Cử Nh&acirc;n v&agrave; Cử Nh&acirc;n Chuy&ecirc;n M&ocirc;n (Licence v&agrave; License Professionnelle)</p>\r\n\r\n<p style="text-align: justify;">- Bằng Thạc Sĩ (Master)</p>\r\n\r\n<p style="text-align: justify;">- Bằng Tiến Sĩ (Doctorat)</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Sinh vi&ecirc;n được cấp bằng Cử Nh&acirc;n sau 6 học kỳ v&agrave; Thạc sĩ th&ecirc;m 4 học kỳ. Sinh vi&ecirc;n phải đạt được một số t&iacute;n chỉ nhất định trong y&ecirc;u cầu để được cấp bằng, v&iacute; dụ Cử Nh&acirc;n l&agrave; 180 v&agrave; Thạc Sĩ l&agrave; th&ecirc;m 120 nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Trường Đại học ở Pháp" src="/usrc/65/9f/_t/659f_truong%20dai%20hoc.JPG" style="margin:5px auto; position:relative; width:90%" title="Trường Đại học ở Pháp" /></p>\r\n\r\n<p style="text-align:center">H&igrave;nh ảnh minh họa một trường Đại học Tổng hợp tại Ph&aacute;p</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Học Ph&iacute;:</strong>&nbsp;</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;">Ch&iacute;nh s&aacute;ch của ch&iacute;nh phủ PH&aacute;p l&agrave; t&agrave;i trợ học ph&iacute; cho cấp Đại học, ch&iacute;nh v&igrave; vậy ở c&aacute;c trường c&ocirc;ng lập của Ph&aacute;p th&igrave; học ph&iacute; chỉ khoảng từ 150 euros đến 700 euros t&ugrave;y thuộc v&agrave;o cấp bậc ( cử nh&acirc;n, thạc sĩ, tiến sĩ ). Hơn thế nữa, với những sinh vi&ecirc;n xuất th&acirc;n từ gia đ&igrave;nh c&oacute; thu nhập thấp, mong muốn học Đại học, c&oacute; thể nộp đơn xin học bổng. T&ugrave;y thu nhập từng gia đ&igrave;nh m&agrave; trợ cấp kh&aacute;c nhau. C&oacute; những gia đ&igrave;nh chỉ phải trả một khoản tiền nhỏ mang t&iacute;nh chất tượng trưng, nhưng cũng c&oacute; những gia đ&igrave;nh được trợ cấp một số tiền l&ecirc;n đến 450 euros/th&aacute;ng. Học ph&iacute; cho những trường đại học c&ocirc;ng đ&agrave;o tạo kỹ sư c&oacute; thể tương đương với c&aacute;c đại học kh&aacute;c, hoặc c&oacute; thể cao hơn một ch&uacute;t, khoảng 700 euro.</p>\r\n\r\n<p style="text-align: justify;">Tuy nhi&ecirc;n những trường đại học tư c&ugrave;ng ng&agrave;nh, học ph&iacute; dao động từ 6000 &ndash; 25000 euros.</p>\r\n\r\n<p style="text-align: justify;">&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><strong>Để hiểu r&otilde; hơn về hệ thống gi&aacute;o dục của Ph&aacute;p, cũng như để được <a href="http://hosoduhocphap.edu.vn/du-hoc-phap">tư vấn miễn ph&iacute; về du học Ph&aacute;p</a> cũng như c&aacute;c nước Ch&acirc;u &Acirc;u, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ :&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t</strong></p>\r\n\r\n<p>Địa chỉ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p>Website &nbsp;&nbsp; :&nbsp;<a href="http://hosoduhocphap.edu.vn/" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p>Email &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;tuvanduhocphapvti@gmail.com</p>\r\n\r\n<p>ĐT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p>FanPage&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p><strong>Hotline &nbsp;:&nbsp;<span style="color:rgb(255, 0, 0)">0983 102 258</span></strong></p>\r\n', 98, 1169452, '2015-04-25 14:10:45', '2015-04-25 16:16:05', '/usrc/a8/8f/_f/a88f_france-education-connect.jpg', '', '', 'hệ thống giáo dục pháp,nền giáo dục pháp,học tại nước pháp', 0, 1, 'hệ thống giáo dục pháp,nền giáo dục pháp,học tại nước pháp ', 'Cung cấp thông tin hệ thống giáo dục của nước Pháp cho các bạn học sinh có một cái nhìn chi tiết hơn về giáo dục tại đây. Tại sao nó lại hấp dẫn du học sinh đến vậy?', 'Tìm hiểu về hệ thống giáo dục của nước Pháp', 'he-thong-giao-duc-cua-nuoc-phap', 1003, '', '', NULL, 0, '/usrc/a8/8f/_f/thumbnail/a88f_france-education-connect.jpg'),
(228, 'Một số nguồn học bổng du học Pháp', 'Pháp là đất nước - là điểm đến mơ ước của rất nhiều sinh viên nói chung và sinh viên Việt Nam nói riêng. Chính vì vậy, học bổng du học Pháp luôn là từ khóa mà tất cả các sinh viên đều quan tâm. Để giúp đỡ sinh viên trong quá trình tìm hiểu các loại học bổng để tìm cho bản thân loại học bổng phù hợp với năng lực, Việt Phát giới thiệu tóm tắt một số loại học bổng dành cho sinh viên có nhu cầu và mong muốn du học tại Pháp.', '<p>&nbsp;</p>\r\n\r\n<p><strong>Học bổng Evariste Galois ( hay c&oacute; t&ecirc;n l&agrave; học bổng Đại sứ qu&aacute;n ) :</strong> l&agrave; <a href="http://hosoduhocphap.edu.vn/mot-so-nguon-hoc-bong-du-hoc-phap"><span style="color:#0000FF"><strong>học bổng của ch&iacute;nh phủ Ph&aacute;p</strong></span></a> do Đại sứ qu&aacute;n tại Việt nam x&eacute;t duyệt.</p>\r\n\r\n<p>Học bổng n&agrave;y d&agrave;nh cho c&aacute;c c&ocirc;ng d&acirc;n Việt Nam dưới 40 tuổi, l&agrave; sinh vi&ecirc;n mới tốt nghiệp đại học hay đang đi l&agrave;m, muốn học tiếp l&ecirc;n chương tr&igrave;nh thạc sĩ hay tiến sĩ tại một trường Đại học của Ph&aacute;p. Mục đ&iacute;ch của học bổng Đại sứ qu&aacute;n l&agrave; gi&uacute;p đ&agrave;o tạo c&aacute;c c&aacute;n bộ kĩ thuật, c&aacute;c nh&agrave; quản l&yacute; kinh tế, h&agrave;nh ch&iacute;nh của Việt Nam trong tương lai. Mỗi năm, học bổng Đại sứ qu&aacute;n sẽ c&oacute; số lượng v&agrave; ng&agrave;nh nghề ưu ti&ecirc;n x&eacute;t duyệt kh&aacute;c nhau, phụ thuộc v&agrave;o định hương v&agrave; nhu cầu ph&aacute;t triển của Việt Nam cũng như quan hệ hợp t&aacute;c song phương Ph&aacute;p &ndash; Việt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Học bổng Đại sứ Quán Pháp" src="/usrc/9e/e6/_h/9ee6_hoc%20bong%20dai%20su%20quan%20phap.jpg" style="margin:5px auto; position:relative; width:90%" title="Học bổng Đại sứ quán Pháp" /></p>\r\n\r\n<p style="text-align:center">Học bổng Đại sứ Qu&aacute;n Ph&aacute;p</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><strong>Học bổng Excellence Eiffel </strong>: l&agrave; học bổng của Bộ ngoại giao Ph&aacute;p v&agrave; do Egide trực tiếp quản l&yacute;. Học bổng d&agrave;nh cho tất cả c&aacute;c sinh vi&ecirc;n nước ngo&agrave;i c&oacute; th&agrave;nh t&iacute;ch xuất sắc, c&oacute; nhu cầu v&agrave; nguyện vọng được đ&agrave;o tạo tr&ecirc;n Đại học tại Ph&aacute;p với 3 lĩnh vực ưu ti&ecirc;n l&agrave; Khoa học, Quản l&yacute; kinh tế v&agrave; Khoa học ch&iacute;nh trị. Với học bổng cho sinh vi&ecirc;n theo chương tr&igrave;nh thạc sĩ bao gồm 1 &ndash; 2 năm v&agrave; th&ecirc;m một năm học tiếng. Với sinh vi&ecirc;n theo chương tr&igrave;nh Tiến sĩ, chương tr&igrave;nh nghi&ecirc;n cứu tối đa l&agrave; 10 th&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Học bổng Eiffel" src="/usrc/91/80/_h/9180_hoc%20bong%20eiffel.jpg" style="margin:5px auto; position:relative; width:90%" title="Học bổng Eiffel" /></p>\r\n\r\n<p style="text-align:center">Học bổng Eiffel</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Học bổng AUF</strong> : l&agrave; học bổng hợp t&aacute;c giữa c&aacute;c Đại học khối Ph&aacute;p ngữ - văn ph&ograve;ng Ch&acirc;u &Aacute; Th&aacute;i B&igrave;nh Dương. C&oacute; 3 loại <strong><span style="color:#0000FF">học bổng</span></strong> d&agrave;nh cho c&aacute;c sinh vi&ecirc;n : học bổng cho đ&agrave;o tạo thạc sĩ, học bổng tiến sĩ v&agrave; học bổng d&agrave;nh cho sinh vi&ecirc;n thực tập chuy&ecirc;n ng&agrave;nh.</p>\r\n\r\n<p><em><span style="color:#800080">+ Học bổng thạc sĩ </span>:</em> d&agrave;nh cho c&aacute;c sinh vi&ecirc;n Việt Nam đ&atilde; tốt nghiệp đại học ở Việt Nam &ndash; nằm trong khối Ph&aacute;p ngữ muốn được đ&agrave;o tạo thạc sĩ tại Ph&aacute;p. ( Th&ocirc;ng thường học bổng cấp trong v&ograve;ng 10 th&aacute;ng v&agrave; kh&ocirc;ng được gia hạn ).</p>\r\n\r\n<p><span style="color:#800080"><em>+ Học bổng tiến sĩ</em> </span>: d&agrave;nh cho c&aacute;c sinh vi&ecirc;n đ&atilde; tốt nghiệp thạc sĩ, c&oacute; nguyện vọng nghi&ecirc;n cứu tiến sĩ ở Ph&aacute;p. Học bổng n&agrave;y ưu ti&ecirc;n cho c&aacute;c đề t&agrave;i đang c&oacute; định hướng ph&aacute;t triển ở nước sở tại.</p>\r\n\r\n<p><span style="color:#800080"><em>+ Học bổng thực tập chuy&ecirc;n ng&agrave;nh : </em></span>d&agrave;nh cho c&aacute;c sinh vi&ecirc;n muốn kh&aacute;m ph&aacute; m&ocirc;i trường doanh nghiệp v&agrave; văn h&oacute;a ở Ph&aacute;p. Học bổng thực tập n&agrave;y được cấp tối đa 3 th&aacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Học bổng AUF" src="/usrc/88/2f/_h/882f_hoc%20bong%20auf.jpg" style="margin:5px auto; position:relative; width:90%" title="Học bổng AUF" /></p>\r\n\r\n<p style="text-align:center">Học bổng AUF</p>\r\n\r\n<p style="text-align:center">&nbsp;</p>\r\n\r\n<p><strong>Học bổng Ch&iacute;nh phủ Việt Nam theo Đề &aacute;n 911</strong>&nbsp;: l&agrave; học bổng d&agrave;nh cho c&aacute;c giảng vi&ecirc;n đại học, cao đẳng, học viện trong cả nước hoặc c&aacute;c nghi&ecirc;n cứu vi&ecirc;n trong c&aacute;c viện nghi&ecirc;n cứu khoa học hoặc sinh vi&ecirc;n vừa tốt nghiệp đại học, thạc sĩ loại kh&aacute; trở l&ecirc;n c&oacute; nguyện vọng v&agrave; cam kết trở th&agrave;nh giảng vi&ecirc;n sau khi kết th&uacute;c qu&aacute; tr&igrave;nh học tại Ph&aacute;p.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Học bổng hợp t&aacute;c giữa Bộ gi&aacute;o dục v&agrave; Đ&agrave;o tạo với PetroVietnam :</strong></p>\r\n\r\n<p>Đ&oacute; l&agrave; <a href="http://hosoduhocphap.edu.vn/mot-so-nguon-hoc-bong-du-hoc-phap"><span style="color:#0000FF"><strong>học bổng </strong></span></a>hỗ trợ cử đi đ&agrave;o tạo ở Ph&aacute;p của Bộ Gi&aacute;o dục v&agrave; Đạo tạo , UBND TP Hồ Ch&iacute; Minh hoặc c&aacute;c doanh nghiệp nh&agrave; nước như PetroVietnam, Vietnam Airlines, Tập đo&agrave;n Điện lực Việt Nam.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Chương tr&igrave;nh học bổng hợp t&aacute;c giữa Bộ Gi&aacute;o dục v&agrave; Đạo tạo với chương tr&igrave;nh học bổng li&ecirc;n kết với c&aacute;c Hội đồng V&ugrave;ng ở Ph&aacute;p</strong> : Đại sứ qu&aacute;n Việt Nam hỗ trợ sinh vi&ecirc;n xin học bổng của Hội đồng c&aacute;c v&ugrave;ng ở Ph&aacute;p như Ile-de-France, Poitou-Charentes.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Để biết th&ocirc;ng tin chi tiết về c&aacute;c loại <a href="http://hosoduhocphap.edu.vn/hoc-bong-du-hoc-phap">học bổng du học Ph&aacute;p</a> cũng như c&aacute;c chương tr&igrave;nh du học Ph&aacute;p - Ch&acirc;u &Acirc;u, c&aacute;c bạn c&oacute; thể li&ecirc;n hệ :&nbsp;</strong></p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t</strong></p>\r\n\r\n<p>Địa chỉ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p>Website &nbsp;&nbsp; :&nbsp;<a href="http://hosoduhocphap.edu.vn/" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p>Email &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;tuvanduhocphapvti@gmail.com</p>\r\n\r\n<p>ĐT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p>FanPage&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p><strong>Hotline &nbsp;:&nbsp;<span style="color:rgb(255, 0, 0)">0983 102 258</span></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 98, 1169454, '2015-04-25 15:16:33', '2015-04-25 16:23:21', '/usrc/64/69/_G/6469_GetArticleImage.jpg', '', '', 'học bổng du học pháp,du học pháp,các loại học bổng tại pháp,hoc bong du hoc phap,du hoc phap,cac loai hoc bong tai phap,hoc bong o phap', 1, 1, 'hoc bong du hoc phap,nguon hoc bong phap,du hoc phap', 'Giới thiệu một số nguồn học bổng du học Pháp cho các bạn học sinh tham khảo và có thể lựa chọn cho bản thân trong quá trình xin học bổng sang pháp.', 'Một số nguồn học bổng du học Pháp', 'mot-so-nguon-hoc-bong-du-hoc-phap', 1004, '', '', NULL, 0, '/usrc/64/69/_G/thumbnail/6469_GetArticleImage.jpg');
INSERT INTO `sa_entry` (`id`, `title`, `description`, `content`, `creator_id`, `category_id`, `create_time`, `update_time`, `image_path`, `facebook_url`, `google_url`, `tags`, `is_top`, `display`, `seo_keyword`, `seo_description`, `seo_title`, `rewrite_url`, `order_number`, `asker`, `asker_email`, `category_title`, `total_views`, `thumbnail_url`) VALUES
(229, 'Các ngành đào tạo trường đại học tổng hợp  Nice – Sophia Antipol', 'Trường đại học Nice – Sophia Antipolis  là một  trường đại học tổng hợp đa ngành tại pháp. Trường  có trụ sở ở thành phố Nice, miền Nam nước Pháp. Hiện nay có khoảng 27000 sinh viên Pháp và quốc tế đang theo học với số lượng sinh viên nước ngoài chiếm tới 30%', '<p>&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Hệ thống trường&nbsp; đại học theo m&ocirc; h&igrave;nh c&aacute;c <a href="http://hosoduhocphap.edu.vn/cac-truong-dai-hoc-tai-phap-phan-1"><span style="color:#0000CD">trường đại học Ph&aacute;p</span></a> bao gồm c&aacute;c khoa, c&aacute;c viện, trường trực thuộc. Đại học&nbsp; Nice &ndash; Sophia Antipolis c&oacute; 7 tổng cộng khoa:</strong></p>\r\n\r\n<p style="text-align:justify">-&nbsp; Khoa học tự nhi&ecirc;n v&agrave; x&atilde; hội</p>\r\n\r\n<p style="text-align:justify">-&nbsp; Khoa&nbsp; Văn học, Nghệ thuật v&agrave; nh&acirc;n văn.</p>\r\n\r\n<p style="text-align:justify">- Khoa Luật v&agrave;&nbsp; Khoa học ch&iacute;nh trị học</p>\r\n\r\n<p style="text-align:justify">- Khoa đ&agrave;o tọa về&nbsp; Y học</p>\r\n\r\n<p style="text-align:justify">- Khoa phẫu thuật thuộc Nha khoa</p>\r\n\r\n<p style="text-align:justify">- Khoa thu&ocirc;c khoa học thể thao</p>\r\n\r\n<p style="text-align:justify">- Khoa Kh&ocirc;ng gian v&agrave;&nbsp; Văn ho&aacute;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Trường Đại học Nice " src="/usrc/90/5f/_t/905f_truong%20dai%20hoc%20Nice.JPG" style="margin:5px auto; position:relative; width:90%" title="Trường Đại học Nice" /></p>\r\n\r\n<p style="text-align:center">Trường Đại học Nice- Sophia Antipolis</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>C&aacute;c viện đại hoc v&agrave; c&aacute;c trường nằm dưới sự quản l&yacute; của <a href="http://hosoduhocphap.edu.vn/cac-nganh-dao-tao-truong-dai-hoc-tong-hop-nice-sophia-antipol">đại học&nbsp; Nice &ndash; Sophia Antipolis</a>:</strong></p>\r\n\r\n<p style="text-align:justify">- Viện Luật về ho&agrave; b&igrave;nh v&agrave;&nbsp; ph&aacute;t triển</p>\r\n\r\n<p style="text-align:justify">- Viện quản trị doanh nghiệp (IAE)</p>\r\n\r\n<p style="text-align:justify">- Viện cao cấp Kinh tế v&agrave;&nbsp; Quản l&yacute;</p>\r\n\r\n<p style="text-align:justify">- Viện đại học c&ocirc;ng nghệ (IUT) &nbsp;mang t&ecirc;n Nice-C&ocirc;te d&rsquo;Azur</p>\r\n\r\n<p style="text-align:justify">- Trường kĩ sư Polytech&rsquo;Nice-Sophia</p>\r\n\r\n<p style="text-align:justify">- Trường cao cấp gi&aacute;o vi&ecirc;n &amp; gi&aacute;o dục (ESPE)</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify"><strong>Ở bậc học cao hơn c&oacute; &nbsp;5 trường Tiến sỹ thuộc ĐH Nice &ndash; Sophia Antipolis:</strong></p>\r\n\r\n<p style="text-align:justify">- Trường Tiến sỹ Khoa học cơ bản v&agrave; &nbsp;ứng dụng</p>\r\n\r\n<p style="text-align:justify">- Trường Tiến sỹ Khoa học -C&ocirc;ng nghệ ứng dụng trong Th&ocirc;ng tin-Truyền th&ocirc;ng</p>\r\n\r\n<p style="text-align:justify">- Trường Tiến sỹ Khoa học đời sống v&agrave; Y tế</p>\r\n\r\n<p style="text-align:justify">- Trường Tiến sỹ Văn học, Nghệ thuật, Khoa học nh&acirc;n văn v&agrave; x&atilde; hội</p>\r\n\r\n<p style="text-align:justify">- Trường Tiến sỹ Luật, Kinh tế, Khoa học ch&iacute;nh trị &amp; Quản l&yacute;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:center"><br />\r\n<img alt="Campus trường Đại học Nice" src="/usrc/fb/0d/_c/fb0d_campus%20dai%20hoc%20Nice.jpg" style="margin:5px auto; position:relative; width:90%" title="Campus trường Đại học Nice" /></p>\r\n\r\n<p style="text-align:center">Campus trường Đại học Nice</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style="text-align:justify">B&ecirc;n cạnh c&aacute;c &nbsp;hệ đ&agrave;o tạo theo &nbsp;ti&ecirc;u chuẩn chung ch&acirc;u &Acirc;u LMD (Licence-Master-Doctorat), đại học&nbsp; Nice &ndash; Sophia Antipolis c&ograve;n phat triển &nbsp;c&aacute;c loại h&igrave;nh đ&agrave;o tạo ngắn hạn m&agrave; sinh vi&ecirc;n Ph&aacute;p v&agrave; quốc tế rất quan t&acirc;m như DUT cũng như c&aacute;c hệ đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh đặc th&ugrave; như &nbsp;Y tế, Kĩ sư v&agrave; c&aacute;c hệ đ&agrave;o tạo từ xa.</p>\r\n\r\n<p style="text-align:justify">&nbsp;</p>\r\n\r\n<p style="text-align:justify">Trường đại học Nice c&oacute; nhiều ng&agrave;nh đ&agrave;o tạo bằng tiếng Anh v&agrave; c&oacute; nhiều ch&iacute;nh s&aacute;ch hỗ trợ cho sinh vi&ecirc;n quốc tế đặc biệt l&agrave; chương tr&igrave;nh thạc sỹ. C&ocirc;ng ty Việt Ph&aacute;t c&oacute;&nbsp; kinh nghiệm l&agrave;m hồ sơ <a href="http://hosoduhocphap.edu.vn/du-hoc-phap"><strong><span style="color:#0000FF">du học Ph&aacute;p</span></strong></a> th&agrave;nh c&ocirc;ng cho c&aacute;c bạn muốn theo học chương tr&igrave;nh thạc sỹ bằng tiếng Anh v&agrave; tiếng Ph&aacute;p. Để biết th&ecirc;m chi tiết về trường đại học Nice, c&aacute;c bạn li&ecirc;n hệ theo địa chỉ:</p>\r\n\r\n<p><strong>C&ocirc;ng ty cổ phần đầu tư v&agrave; ph&aacute;t triển thương mại Việt Ph&aacute;t</strong></p>\r\n\r\n<p>Địa chỉ&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Số 48, ng&aacute;ch 2, ng&otilde; Simco, đường Phạm H&ugrave;ng, H&agrave; Nội</p>\r\n\r\n<p>Website &nbsp;&nbsp; :&nbsp;<a href="http://hosoduhocphap.edu.vn/" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">http://hosoduhocphap.edu.vn/</a></p>\r\n\r\n<p>Email &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;tuvanduhocphapvti@gmail.com</p>\r\n\r\n<p>ĐT &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;: &nbsp;&nbsp;04 62 96 75 42</p>\r\n\r\n<p>FanPage&nbsp; : &nbsp;&nbsp;<a href="https://www.facebook.com/DuhocVietPhatVTI" style="box-sizing: border-box; font-size: 14px; text-decoration: none; margin: 0px; padding: 0px; color: blue !important; background: transparent;">https://www.facebook.com/DuhocVietPhatVTI</a></p>\r\n\r\n<p><strong>Hotline &nbsp;:&nbsp;<span style="color:rgb(255, 0, 0)">0983 102 258</span></strong></p>\r\n', 98, 1169448, '2015-04-25 15:39:54', '2015-04-25 16:27:28', '/usrc/99/0d/_u/990d_unslogo.jpg', '', '', 'du học pháp,học đại học tại pháp,học thạc sĩ tại pháp,thông tin du học pháp,trường đại học nice,Nice sophia antipolis,du hoc phap,hoc dai hoc tai phap,hoc thac si tai phap,thong tin du hoc phap,truong dai hoc nice', 0, 1, 'du học pháp, học đại học tại pháp,học thạc sĩ tại pháp,thông tin du học pháp,trường đại học nice,Nice sophia antipolis,du hoc phap,hoc dai hoc tai phap', 'Trường đại học Nice – Sophia Antipolis  là một  trường đại học tổng hợp đa ngành tại pháp dành cho các bạn học sinh muốn học bằng thạc sĩ có thể tham khảo.', 'Các ngành đào tạo trường đại học tổng hợp  Nice – Sophia Antipol', 'cac-nganh-dao-tao-truong-dai-hoc-tong-hop-nice-sophia-antipol', 1005, '', '', NULL, 0, '/usrc/99/0d/_u/thumbnail/990d_unslogo.jpg'),
(230, 'Các trường Đại học tại Pháp ( Phần 1 )', 'Pháp là một nước có nền giáo dục đa dạng và phong phú với hệ thống các trường Đại học trải rộng trên khắp cả nước. Để đáp ứng nhu cầu tìm hiểu thông tin du học Pháp của học sinh - sinh viên, Việt Phát giới thiệu cho các bạn một số trường Đại học tại Pháp.', '<p>&nbsp;</p>\r\n\r\n<h2><span style="font-size:16px"><strong>Danh s&aacute;ch c&aacute;c trường đại học tại Ph&aacute;p</strong></span></h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border="1" cellpadding="1" cellspacing="1" style="width:500px">\r\n	<tbody>\r\n		<tr>\r\n			<td style="text-align:center">T&ecirc;n</td>\r\n			<td style="text-align:center">Địa chỉ</td>\r\n			<td style="text-align:center">Email</td>\r\n			<td style="text-align:center">Fax</td>\r\n			<td style="text-align:center">Ng&agrave;nh đ&agrave;o tạo</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Bordeaux International Wine Institute</strong></p>\r\n\r\n			<p><span style="color:rgb(0, 0, 255)">http://www.wine-institute.com</span></p>\r\n			</td>\r\n			<td>\r\n			<p>Hangar 18 quai de Bacalan</p>\r\n\r\n			<p>33070 Bordeaux</p>\r\n\r\n			<p>Gironde</p>\r\n			</td>\r\n			<td>&nbsp;</td>\r\n			<td>Fax : +33556007372</td>\r\n			<td>Bachelor commercialisation des vins et spiritueux</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Ecole sup&eacute;rieure d&#39;agriculture d&#39;Angers</strong></p>\r\n\r\n			<p><span style="color:rgb(0, 0, 255)">http://www.groupe-esa.com</span></p>\r\n\r\n			<div>&nbsp;</div>\r\n			</td>\r\n			<td>\r\n			<p>55 rue Rabelais</p>\r\n\r\n			<p>49007 Angers Cedex 01</p>\r\n\r\n			<p>Maine et Loire</p>\r\n			</td>\r\n			<td>&nbsp;</td>\r\n			<td>Fax : +33241235500</td>\r\n			<td>Bachelor of Sciences European Engineer Degree productions animales, productions v&eacute;g&eacute;tales, international agribusiness</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Universit&eacute; Blaise Pascal</strong></p>\r\n\r\n			<p>34, avenue Carnot - BP 185<br />\r\n			63006 CLERMON