  `create_time` datetime DEFAULT NULL,
  `update_time` datetime DEFAULT NULL,
  `is_top` tinyint(1) DEFAULT NULL,
  `category_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=18 ;

