(6, 1, '', 'header', 'Học đại học tại Pháp', 'Thông tin các trường đại học tại pháp, thông tin học bổng dành cho các bạn học sinh tham khảo để có được sự lựa chọn và đáp ứng được nguyện vọng đi du học của mình.', 1169448, 'one_category', 1, '2015-02-11 10:58:45', '2015-04-25 15:24:22', 0, '', '', '', '', '', 'hoc-dai-hoc-tai-phap'),
(7, 1, '', 'header', 'Học cao học tại Pháp', 'Thông tin các trường đại học tại pháp, thông tin học bổng dành cho các bạn học sinh muốn học cao học tại Pháp tham khảo để có được sự lựa chọn và đáp ứng được nguyện vọng đi du học của mình.', 1169449, 'one_category', 1, '2015-02-11 10:59:12', '2015-04-25 15:25:36', 1, '', '', '', '', '', 'hoc-cao-hoc-tai-phap'),
(8, 1, '', 'header', 'Học dự bị tại Pháp', 'Thông tin các trường, thủ tục làm hồ sơ, cách xin thị thực cho việc học dự bị tại đất nước Pháp. Đảm bảo một tương lai tương sáng cho học sinh Việt Nam.', 1138380, 'one_category', 1, '2015-02-11 10:59:35', '2015-04-25 15:27:43', 2, '', '', '', '', '', 'hoc-du-bi-tai-phap'),
(14, 0, '', 'footer', 'DU HỌC PHÁP', '', 1132795, 'one_category', 1, '2015-02-11 17:01:19', '2015-04-01 00:09:02', 4, '', '', '', '', '', 'du-hoc-phap'),
(15, 0, '', 'footer', 'Học tiếng Pháp tại Việt Phát', '', 1138380, 'one_category', 1, '2015-02-11 17:01:44', '2015-04-20 01:59:43', 5, '', '', '', '', '', 'hoc-tieng-phap-tai-viet-phat'),
