(36, 3, '', 'header', 'Văn hóa - Nghệ thuật', '', 1169451, 'one_category', 1, '2015-04-23 10:40:39', '2015-04-23 10:40:39', 2, '', '', '', '', '', 'van-hoa-nghe-thuat'),
(37, 3, '', 'header', 'Nền giáo dục Pháp', '', 1169452, 'one_category', 1, '2015-04-23 10:46:35', '2015-04-23 10:46:35', 3, '', '', '', '', '', 'nen-giao-duc-phap'),
(40, 26, '', 'header', 'Thủ tục đăng ký', '', 1169460, 'one_category', 1, '2015-04-23 13:25:59', '2015-04-23 13:25:59', 1, '', '', '', '', '', 'thu-tuc-dang-ky'),
(41, 26, '', 'header', 'Phỏng vấn', '', 1169461, 'one_category', 1, '2015-04-23 13:26:36', '2015-04-23 13:26:36', 2, '', '', '', '', '', 'phong-van'),
(42, 26, '', 'header', 'Visa du học Pháp', '', 1169462, 'one_category', 1, '2015-04-23 13:27:03', '2015-04-23 13:27:38', 18, '', '', '', '', '', 'visa-du-hoc-phap'),
(47, 1, '', 'header', 'Học tiếng Pháp', 'Thông tin các khóa học tiếng Pháp do công ty Việt Phát tổ chức, lớp học tiếng pháp cấp tốc, lớp luyện thi tcf, luyện cách phát âm một cách nhuần nhuyễn nhất.', 1169466, 'one_category', 1, '2015-04-23 14:22:53', '2015-04-25 15:31:12', 4, '', '', '', '', '', 'hoc-tieng-phap'),
(48, 1, '', 'header', 'Học nghề tại Pháp', 'Cung cấp thông tin các chương trình học BTF, chương trình IUT dành cho các bạn có ý định du học có được sự chuẩn bị tốt nhất trước khi sang Pháp học nghề.', 1169465, 'one_category', 1, '2015-04-24 16:16:14', '2015-04-25 15:29:30', 3, '', '', '', '', '', 'hoc-nghe-tai-phap'),
(49, 1, '', 'header', 'Cẩm nang du học', 'Nhưng thông tin bổ ích về việc đi du học tại Pháp, những chia sẻ kinh nghiệm quý báu trong việc sinh sống và học tập tại đất nước có nền giáo dục hiện đại và thành công.', 1169467, 'one_category', 1, '2015-04-25 15:33:40', '2015-04-25 15:36:28', 19, '', '', '', '', '', 'cam-nang-du-hoc-phap'),
(50, 2, '', 'header', 'Việc làm ở Pháp', 'Thông tin các việc làm thêm tại Pháp cho những bạn học sinh có ý định làm thêm khi du học tại đây, nhằm trang trải chi phí học tập và sinh hoạt cũng như cải thiện khả năng giao tiếp.', 1169468, 'one_category', 1, '2015-04-25 15:41:59', '2015-04-25 15:45:11', 7, '', '', '', '', '', 'viec-lam-o-phap'),
